import{b as e}from"./app.9da19d67.js";import{_ as o}from"./plugin-vue_export-helper.21dcd24c.js";const c={},r=e('<h1 id="java-8\u5E76\u53D1\u5305\u4E0B\u5E38\u89C1\u7684\u5E76\u53D1\u7C7B" tabindex="-1"><a class="header-anchor" href="#java-8\u5E76\u53D1\u5305\u4E0B\u5E38\u89C1\u7684\u5E76\u53D1\u7C7B" aria-hidden="true">#</a> Java 8\u5E76\u53D1\u5305\u4E0B\u5E38\u89C1\u7684\u5E76\u53D1\u7C7B</h1><h2 id="\u963B\u585E\u961F\u5217" tabindex="-1"><a class="header-anchor" href="#\u963B\u585E\u961F\u5217" aria-hidden="true">#</a> \u963B\u585E\u961F\u5217</h2><ul><li><code>LinkedBlockingDeque</code>\uFF1A\u5E95\u5C42\u662F\u5355\u5411\u94FE\u8868\u5B9E\u73B0\uFF0C\u963B\u585E\u53CC\u7AEF\u961F\u5217\uFF0C\u6709\u4E24\u628A\u9501\uFF0C\u4E00\u628A\u7528\u4E8E\u5BF9\u5934\u79FB\u9664\u5143\u7D20\uFF0C\u4E00\u628A\u7528\u4E8E\u6DFB\u52A0\u5143\u7D20\uFF0C\u6DFB\u52A0\u79FB\u9664\u53EF\u4EE5\u5E76\u53D1\u6267\u884C\uFF0C\u4E24\u628A\u9501\u6BD4\u4E00\u628A\u9501\u5E76\u53D1\u5EA6\u9AD8\uFF1B\u6CE8\u610F\uFF1A\u8FED\u4EE3\u6574\u4E2A\u961F\u5217\u6216\u67E5\u627E\u4E2D\u95F4\u5143\u7D20\u65F6\u9700\u8981\u540C\u65F6\u62FF\u5230\u4E24\u628A\u9501\uFF0C\u5C3D\u91CF\u51CF\u5C11\u8FD9\u4E9B\u64CD\u4F5C\u3002</li><li><code>BlockingQueue</code>\uFF1A\u963B\u585E\u961F\u5217\u63A5\u53E3</li><li><code>BlockingDeque</code>\uFF1A\u53CC\u7AEF\u963B\u585E\u961F\u5217\u63A5\u53E3</li><li><code>ArrayBlockingQueue</code>\uFF1A\u963B\u585E\u961F\u5217\uFF0C\u6570\u7EC4\u5B9E\u73B0</li><li><code>LinkedBlockingQueue</code>\uFF1A\u963B\u585E\u961F\u5217\uFF0C\u94FE\u8868\u5B9E\u73B0</li><li><code>DelayQueue</code>\uFF1A\u963B\u585E\u961F\u5217\uFF0C\u5E76\u4E14\u5143\u7D20\u662F Delay \u7684\u5B50\u7C7B\uFF0C\u4FDD\u8BC1\u5143\u7D20\u5728\u8FBE\u5230\u4E00\u5B9A\u65F6\u95F4\u540E\u624D\u53EF\u4EE5\u53D6\u5F97\u5230</li><li><code>PriorityBlockingQueue</code>\uFF1A\u4F18\u5148\u7EA7\u963B\u585E\u961F\u5217</li><li><code>SynchronousQueue</code>\uFF1A\u540C\u6B65\u961F\u5217\uFF0C\u4F46\u662F\u961F\u5217\u957F\u5EA6\u4E3A 0\uFF0C\u751F\u4EA7\u8005\u653E\u5165\u961F\u5217\u7684\u64CD\u4F5C\u4F1A\u88AB\u963B\u585E\uFF0C\u76F4\u5230\u6D88\u8D39\u8005\u8FC7\u6765\u53D6\uFF0C\u6240\u4EE5\u8FD9\u4E2A\u961F\u5217\u6839\u672C\u4E0D\u9700\u8981\u7A7A\u95F4\u5B58\u653E\u5143\u7D20\uFF1B\u6709\u70B9\u50CF\u4E00\u4E2A\u72EC\u6728\u6865\uFF0C\u4E00\u6B21\u53EA\u80FD\u4E00\u4EBA\u901A\u8FC7\uFF0C\u8FD8\u4E0D\u80FD\u5728\u6865\u4E0A\u505C\u7559</li></ul><h2 id="\u975E\u963B\u585E\u961F\u5217" tabindex="-1"><a class="header-anchor" href="#\u975E\u963B\u585E\u961F\u5217" aria-hidden="true">#</a> \u975E\u963B\u585E\u961F\u5217</h2><ul><li><code>ConcurrentLinkedDeque</code>\uFF1A\u975E\u963B\u585E\u53CC\u7AEF\u961F\u5217\uFF0C\u94FE\u8868\u5B9E\u73B0\uFF0C\u901A\u8FC7<code>volatile</code>\u5173\u952E\u5B57\u548CCAS\u64CD\u4F5C\u5B9E\u73B0\u7684\u3002\u541E\u5410\u91CF\u4F1A\u9AD8\uFF0C\u5E76\u53D1\u65F6\u901A\u8FC7\u81EA\u65CB\u4FDD\u8BC1\u5B89\u5168\uFF1B</li><li><code>ConcurrentLinkedQueue</code>\uFF1A\u975E\u963B\u585E\u961F\u5217\uFF0C\u94FE\u8868\u5B9E\u73B0</li></ul><h2 id="\u8F6C\u79FB\u961F\u5217" tabindex="-1"><a class="header-anchor" href="#\u8F6C\u79FB\u961F\u5217" aria-hidden="true">#</a> \u8F6C\u79FB\u961F\u5217</h2><ul><li><code>TransferQueue</code>\uFF1A\u8F6C\u79FB\u961F\u5217\u63A5\u53E3\uFF0C\u751F\u4EA7\u8005\u8981\u7B49\u6D88\u8D39\u8005\u6D88\u8D39\u7684\u961F\u5217\uFF0C\u751F\u4EA7\u8005\u5C1D\u8BD5\u628A\u5143\u7D20\u76F4\u63A5\u8F6C\u79FB\u7ED9\u6D88\u8D39\u8005</li><li><code>LinkedTransferQueue</code>\uFF1A\u8F6C\u79FB\u961F\u5217\u7684\u94FE\u8868\u5B9E\u73B0\uFF0C\u5B83\u6BD4 SynchronousQueue \u66F4\u5FEB</li></ul><h2 id="\u5176\u5B83\u5BB9\u5668" tabindex="-1"><a class="header-anchor" href="#\u5176\u5B83\u5BB9\u5668" aria-hidden="true">#</a> \u5176\u5B83\u5BB9\u5668</h2><ul><li><code>ConcurrentHashMap</code>\uFF1A\u5E76\u53D1 HashMap</li><li><code>ConcurrentMap</code>\uFF1A\u5E76\u53D1 Map \u7684\u63A5\u53E3\uFF0C\u5B9A\u4E49\u4E86 <code>putIfAbsent(k,v)</code>\u3001remove(k,v)\u3001replace(k,oldV,newV)\u3001replace(k,v) \u8FD9\u56DB\u4E2A\u5E76\u53D1\u573A\u666F\u4E0B\u7279\u5B9A\u7684\u65B9\u6CD5</li><li><code>ConcurrentNavigableMap</code>\uFF1ANavigableMap \u7684\u5B9E\u73B0\u7C7B\uFF0C\u8FD4\u56DE\u6700\u63A5\u8FD1\u7684\u4E00\u4E2A\u5143\u7D20</li><li><code>ConcurrentSkipListMap</code>\uFF1A\u5B83\u4E5F\u662F <code>NavigableMap</code> \u7684\u5B9E\u73B0\u7C7B(\u8981\u6C42\u5143\u7D20\u4E4B\u95F4\u53EF\u4EE5\u6BD4\u8F83)\uFF0C\u540C\u65F6\u5B83\u6BD4 <code>ConcurrentHashMap</code> \u66F4\u52A0 <code>scalable\u2014\u2014ConcurrentHashMap</code> \u5E76\u4E0D\u4FDD\u8BC1\u5B83\u7684\u64CD\u4F5C\u65F6\u95F4\uFF0C\u5E76\u4E14\u4F60\u53EF\u4EE5\u81EA\u5DF1\u6765\u8C03\u6574\u5B83\u7684 <code>load factor</code>\uFF1B\u4F46\u662F <code>ConcurrentSkipListMap</code> \u53EF\u4EE5\u4FDD\u8BC1 <code>O(log n)</code> \u7684\u6027\u80FD\uFF0C\u540C\u65F6\u4E0D\u80FD\u81EA\u5DF1\u6765\u8C03\u6574\u5B83\u7684\u5E76\u53D1\u53C2\u6570\uFF0C\u53EA\u6709\u4F60\u786E\u5B9E\u9700\u8981\u5FEB\u901F\u7684\u904D\u5386\u64CD\u4F5C\uFF0C\u5E76\u4E14\u53EF\u4EE5\u627F\u53D7\u989D\u5916\u7684\u63D2\u5165\u5F00\u9500\u7684\u65F6\u5019\uFF0C\u624D\u53BB\u4F7F\u7528\u5B83</li><li><code>ConcurrentSkipListSet</code>\uFF1A\u548C\u4E0A\u9762\u7C7B\u4F3C\uFF0C\u53EA\u4E0D\u8FC7 map \u53D8\u6210\u4E86 set</li><li><code>CopyOnWriteArrayList</code>\uFF1A<code>copy-on-write</code>\u6A21\u5F0F\u7684 array list\uFF0C\u6BCF\u5F53\u9700\u8981\u63D2\u5165\u5143\u7D20\uFF0C\u800C\u662F\u4F1A<strong>\u590D\u5236</strong>\u4E00\u4E2A list\u8FDB\u884C\u5199\u64CD\u4F5C\uFF0C\u9002\u5408\u8BFB\u8FDC\u5927\u4E8E\u5199\u7684\u573A\u666F\uFF0C\u8BFB\u5199\u5206\u79BB\u7684\u601D\u60F3\uFF0C\u8986\u76D6\u539F\u6765\u7684volatile\u5F15\u7528\u3002</li><li><code>CopyOnWriteArraySet</code>\uFF1A\u548C\u4E0A\u9762\u7C7B\u4F3C\uFF0Clist \u53D8\u6210 set \u800C\u5DF2</li></ul><h2 id="\u540C\u6B65\u8BBE\u5907" tabindex="-1"><a class="header-anchor" href="#\u540C\u6B65\u8BBE\u5907" aria-hidden="true">#</a> \u540C\u6B65\u8BBE\u5907</h2><p>\u8FD9\u4E9B\u7C7B\u5927\u90E8\u5206\u90FD\u662F\u5E2E\u52A9\u505A\u7EBF\u7A0B\u4E4B\u95F4\u540C\u6B65\u7684\uFF0C\u7B80\u5355\u63CF\u8FF0\uFF0C\u5C31\u50CF\u662F\u63D0\u4F9B\u4E86\u4E00\u4E2A\u7BF1\u7B06\uFF0C\u7EBF\u7A0B\u6267\u884C\u5230\u8FD9\u4E2A\u7BF1\u7B06\u7684\u65F6\u5019\u90FD\u5F97\u7B49\u4E00\u7B49\uFF0C\u7B49\u5230\u6761\u4EF6\u6EE1\u8DB3\u4EE5\u540E\u518D\u5F80\u540E\u8D70\u3002</p><p><code>CountDownLatch</code>\uFF1A\u4E00\u4E2A\u7EBF\u7A0B\u8C03\u7528 await \u65B9\u6CD5\u4EE5\u540E\uFF0C\u4F1A\u963B\u585E\u5730\u7B49\u5F85\u8BA1\u6570\u5668\u88AB\u8C03\u7528 countDown \u76F4\u5230\u53D8\u6210 0\uFF0C\u529F\u80FD\u4E0A\u548C\u4E0B\u9762\u7684 CyclicBarrier \u6709\u70B9\u50CF</p><p><code>CyclicBarrier</code>\uFF1A\u4E5F\u662F\u8BA1\u6570\u7B49\u5F85\uFF0C\u53EA\u4E0D\u8FC7\u5B83\u662F\u5229\u7528 await \u65B9\u6CD5\u672C\u8EAB\u6765\u5B9E\u73B0\u8BA1\u6570\u5668\u201C+1\u201D \u7684\u64CD\u4F5C\uFF0C\u4E00\u65E6\u8BA1\u6570\u5668\u4E0A\u663E\u793A\u7684\u6570\u5B57\u8FBE\u5230 Barrier \u53EF\u4EE5\u6253\u7834\u7684\u754C\u9650\uFF0C\u5C31\u4F1A\u629B\u51FA BrokenBarrierException\uFF0C\u7EBF\u7A0B\u5C31\u53EF\u4EE5\u7EE7\u7EED\u5F80\u4E0B\u6267\u884C\uFF1B\u8BF7\u53C2\u89C1\u6211\u5199\u8FC7\u7684\u8FD9\u7BC7\u6587\u7AE0 \u300A\u540C\u6B65\u3001\u5F02\u6B65\u8F6C\u5316\u548C\u4EFB\u52A1\u6267\u884C\u300B\u4E2D\u7684 Barrier \u6A21\u5F0F</p><p><code>Semaphore</code>\uFF1A\u529F\u80FD\u4E0A\u5F88\u7B80\u5355\uFF0Cacquire() \u548C release() \u4E24\u4E2A\u65B9\u6CD5\uFF0C\u4E00\u4E2A\u5C1D\u8BD5\u83B7\u53D6\u8BB8\u53EF\uFF0C\u4E00\u4E2A\u91CA\u653E\u8BB8\u53EF\uFF0CSemaphore \u6784\u9020\u65B9\u6CD5\u63D0\u4F9B\u4E86\u4F20\u5165\u4E00\u4E2A\u8868\u793A\u8BE5\u4FE1\u53F7\u91CF\u6240\u5177\u5907\u7684\u8BB8\u53EF\u6570\u91CF\u3002</p><p><code>Exchanger</code>\uFF1A\u8FD9\u4E2A\u7C7B\u7684\u5B9E\u4F8B\u5C31\u50CF\u662F\u4E24\u5217\u98DE\u9A70\u7684\u706B\u8F66(\u7EBF\u7A0B)\u4E4B\u95F4\u5F00\u4E86\u4E00\u4E2A\u795E\u5947\u7684\u5C0F\u7A97\u53E3\uFF0C\u901A\u8FC7\u5C0F\u7A97\u53E3(exchange \u65B9\u6CD5)\u53EF\u4EE5\u8BA9\u4E24\u5217\u706B\u8F66\u5B89\u5168\u5730\u4EA4\u6362\u6570\u636E\u3002</p><p><code>Phaser</code>\uFF1A\u529F\u80FD\u4E0A\u548C\u7B2C 1\u30012 \u4E2A\u5DEE\u4E0D\u591A\uFF0C\u4F46\u662F\u53EF\u4EE5\u91CD\u7528\uFF0C\u4E14\u66F4\u52A0\u7075\u6D3B\uFF0C\u7A0D\u5FAE\u6709\u70B9\u590D\u6742(CountDownLatch \u662F\u4E0D\u65AD-1\uFF0CCyclicBarrier \u662F\u4E0D\u65AD+1\uFF0C\u800C Phaser \u5B9A\u4E49\u4E86\u4E24\u4E2A\u6982\u5FF5\uFF0Cphase \u548C party)\uFF0C\u6211\u5728\u4E0B\u9762\u753B\u4E86\u5F20\u56FE\uFF0C\u5E0C\u671B\u80FD\u591F\u5E2E\u52A9\u7406\u89E3\uFF1A</p><p>\u4E00\u4E2A\u662F phase\uFF0C\u8868\u793A\u5F53\u524D\u5728\u54EA\u4E00\u4E2A\u9636\u6BB5\uFF0C\u6BCF\u78B0\u5230\u4E00\u6B21 barrier \u5C31\u4F1A\u89E6\u53D1 advance \u64CD\u4F5C(\u89E6\u53D1\u524D\u8C03\u7528 onAdvance \u65B9\u6CD5)\uFF0C\u4E00\u65E6\u8D8A\u8FC7\u8FD9\u9053 barrier \u5C31\u4F1A\u89E6\u53D1 phase+1\uFF0C\u8FD9\u5F88\u5BB9\u6613\u7406\u89E3\uFF1B \u53E6\u4E00\u4E2A\u662F party\uFF0C\u5F88\u591A\u6587\u7AE0\u8BF4\u5B83\u5C31\u662F\u7EBF\u7A0B\u6570\uFF0C\u4F46\u662F\u5176\u5B9E\u8FD9\u5E76\u4E0D\u51C6\u786E\uFF0C\u5B83\u66F4\u50CF\u4E00\u4E2A\u7528\u4E8E\u5224\u65AD advance \u662F\u5426\u88AB\u5141\u8BB8\u53D1\u751F\u7684\u8BA1\u6570\u5668\uFF1A \u4EFB\u4F55\u65F6\u5019\u90FD\u6709\u4E00\u4E2A party \u7684\u603B\u6570\uFF0C\u5373\u6CE8\u518C(registered)\u7684 party \u6570\uFF0C\u5B83\u53EF\u4EE5\u5728 Phaser \u6784\u9020\u5668\u91CC\u6307\u5B9A\uFF0C\u4E5F\u53EF\u4EE5\u4EFB\u610F\u65F6\u523B\u8C03\u7528\u65B9\u6CD5\u52A8\u6001\u589E\u51CF\uFF1B \u6BCF\u4E00\u4E2A party \u90FD\u6709 unarrived \u548C arrived \u4E24\u79CD\u72B6\u6001\uFF0C\u53EF\u4EE5\u901A\u8FC7\u8C03\u7528 arriveXXX \u65B9\u6CD5\u4F7F\u5F97\u5B83\u4ECE unarrived \u53D8\u6210 arrived\uFF1B \u6BCF\u4E00\u4E2A\u7EBF\u7A0B\u5230\u8FBE barrier \u540E\u4F1A\u7B49\u5F85(\u8C03\u7528 arriveAndAwaitAdvance \u65B9\u6CD5)\uFF0C\u4E00\u65E6\u6240\u6709 party \u90FD\u5230\u8FBE(\u5373 arrived \u7684 party \u6570\u91CF\u7B49\u4E8E registered \u7684\u6570\u91CF)\uFF0C\u5C31\u4F1A\u89E6\u53D1 advance \u64CD\u4F5C\uFF0C\u540C\u65F6 barrier \u88AB\u6253\u7834\uFF0C\u7EBF\u7A0B\u7EE7\u7EED\u5411\u4E0B\u6267\u884C\uFF0Cparty \u91CD\u65B0\u53D8\u4E3A unarrived \u72B6\u6001\uFF0C\u91CD\u65B0\u7B49\u5F85\u6240\u6709 party \u7684\u5230\u8FBE\uFF1B \u5728\u7EDD\u5927\u591A\u6570\u60C5\u51B5\u4E0B\u4E00\u4E2A\u7EBF\u7A0B\u5C31\u53EA\u8D1F\u8D23\u64CD\u63A7\u4E00\u4E2A party \u7684\u5230\u8FBE\uFF0C\u56E0\u6B64\u5F88\u591A\u6587\u7AE0\u8BF4 party \u6307\u7684\u5C31\u662F\u7EBF\u7A0B\uFF0C\u4F46\u662F\u8FD9\u662F\u4E0D\u51C6\u786E\u7684\uFF0C\u56E0\u4E3A\u4E00\u4E2A\u7EBF\u7A0B\u5B8C\u5168\u53EF\u4EE5\u64CD\u63A7\u591A\u4E2A party\uFF0C\u53EA\u8981\u5B83\u6267\u884C\u591A\u6B21\u7684 arrive \u65B9\u6CD5\u3002</p><h2 id="\u539F\u5B50\u5BF9\u8C61" tabindex="-1"><a class="header-anchor" href="#\u539F\u5B50\u5BF9\u8C61" aria-hidden="true">#</a> \u539F\u5B50\u5BF9\u8C61</h2><p>\u8FD9\u4E9B\u5BF9\u8C61\u90FD\u7684\u884C\u4E3A\u5728\u4E0D\u4F7F\u7528\u540C\u6B65\u7684\u60C5\u51B5\u4E0B\u4FDD\u8BC1\u4E86\u539F\u5B50\u6027\u3002\u503C\u5F97\u4E00\u63D0\u7684\u6709\u4E24\u70B9\uFF1A</p><p>weakCompareAndSet \u65B9\u6CD5\uFF1AcompareAndSet \u65B9\u6CD5\u5F88\u660E\u786E\uFF0C\u4F46\u662F\u8FD9\u4E2A\u662F\u5565\uFF1F\u6839\u636E JSR \u89C4\u8303\uFF0C\u8C03\u7528 weakCompareAndSet \u65F6\u5E76\u4E0D\u80FD\u4FDD\u8BC1 happen-before \u7684\u4E00\u81F4\u6027\uFF0C\u56E0\u6B64\u5141\u8BB8\u5B58\u5728\u91CD\u6392\u5E8F\u6307\u4EE4\u7B49\u7B49\u865A\u62DF\u673A\u4F18\u5316\u5BFC\u81F4\u8FD9\u4E2A\u64CD\u4F5C\u5931\u8D25(\u8F83\u5F31\u7684\u539F\u5B50\u66F4\u65B0\u64CD\u4F5C)\uFF0C\u4F46\u662F\u4ECE Java \u6E90\u4EE3\u7801\u770B\uFF0C\u5B83\u7684\u5B9E\u73B0\u5176\u5B9E\u548C compareAndSet \u662F\u4E00\u6A21\u4E00\u6837\u7684\uFF1B</p><p>lazySet \u65B9\u6CD5\uFF1A\u5EF6\u65F6\u8BBE\u7F6E\u53D8\u91CF\u503C\uFF0C\u8FD9\u4E2A\u7B49\u4EF7\u4E8E set \u65B9\u6CD5\uFF0C\u4F46\u662F\u7531\u4E8E\u5B57\u6BB5\u662F volatile \u7C7B\u578B\u7684\uFF0C\u56E0\u6B64\u6B21\u5B57\u6BB5\u7684\u4FEE\u6539\u4F1A\u6BD4\u666E\u901A\u5B57\u6BB5(\u975E volatile \u5B57\u6BB5)\u6709\u7A0D\u5FAE\u7684\u6027\u80FD\u635F\u8017\uFF0C\u6240\u4EE5\u5982\u679C\u4E0D\u9700\u8981\u7ACB\u5373\u8BFB\u53D6\u8BBE\u7F6E\u7684\u65B0\u503C\uFF0C\u90A3\u4E48\u6B64\u65B9\u6CD5\u5C31\u5F88\u6709\u7528\u3002</p><ul><li><code>AtomicBoolean</code></li><li><code>AtomicInteger</code></li><li><code>AtomicIntegerArray</code></li><li><code>AtomicIntegerFieldUpdater</code></li><li><code>AtomicLong</code></li><li><code>AtomicLongArray</code></li><li><code>AtomicLongFieldUpdater</code></li><li><code>AtomicMarkableReference</code>\uFF0C\u5B83\u662F\u7528\u6765\u9AD8\u6548\u8868\u8FF0 Object-boolean \u8FD9\u6837\u7684\u5BF9\u8C61\u6807\u5FD7\u4F4D\u6570\u636E\u7ED3\u6784\u7684\uFF0C\u4E00\u4E2A\u5BF9\u8C61\u5F15\u7528+\u4E00\u4E2A bit \u6807\u5FD7\u4F4D</li><li><code>AtomicReference</code></li><li><code>AtomicReferenceArray</code></li><li><code>AtomicReferenceFieldUpdater</code></li><li><code>AtomicStampedReference</code>\uFF0C\u5B83\u548C\u524D\u9762\u7684 AtomicMarkableReference \u7C7B\u4F3C\uFF0C\u4F46\u662F\u5B83\u662F\u7528\u6765\u9AD8\u6548\u8868\u8FF0 Object-int \u8FD9\u6837\u7684\u201C \u5BF9\u8C61+\u7248\u672C\u53F7\u201D \u6570\u636E\u7ED3\u6784\uFF0C\u7279\u522B\u7528\u4E8E\u89E3\u51B3 ABA \u95EE\u9898(ABA \u95EE\u9898\u8FD9\u7BC7\u6587\u7AE0\u91CC\u9762\u4E5F\u6709\u4ECB\u7ECD)</li></ul><h2 id="\u9501" tabindex="-1"><a class="header-anchor" href="#\u9501" aria-hidden="true">#</a> \u9501</h2><p>AbstractOwnableSynchronizer\uFF0C\u8FD9\u4E09\u4E2A AbstractXXXSynchronizer \u90FD\u662F\u4E3A\u4E86\u521B\u5EFA\u9501\u548C\u76F8\u5173\u7684\u540C\u6B65\u5668\u800C\u63D0\u4F9B\u7684\u57FA\u7840\uFF0C\u9501\uFF0C\u8FD8\u6709\u524D\u9762\u63D0\u5230\u7684\u540C\u6B65\u8BBE\u5907\u90FD\u501F\u7528\u4E86\u5B83\u4EEC\u7684\u5B9E\u73B0\u903B\u8F91</p><p>AbstractQueuedLongSynchronizer\uFF0CAbstractOwnableSynchronizer \u7684\u5B50\u7C7B\uFF0C\u6240\u6709\u7684\u540C\u6B65\u72B6\u6001\u90FD\u662F\u7528 long \u53D8\u91CF\u6765\u7EF4\u62A4\u7684\uFF0C\u800C\u4E0D\u662F int\uFF0C\u5728\u9700\u8981 64 \u4F4D\u7684\u5C5E\u6027\u6765\u8868\u793A\u72B6\u6001\u7684\u65F6\u5019\u4F1A\u5F88\u6709\u7528</p><p>AbstractQueuedSynchronizer\uFF0C\u4E3A\u5B9E\u73B0\u4F9D\u8D56\u4E8E\u5148\u8FDB\u5148\u51FA\u961F\u5217\u7684\u963B\u585E\u9501\u548C\u76F8\u5173\u540C\u6B65\u5668(\u4FE1\u53F7\u91CF\u3001\u4E8B\u4EF6\u7B49\u7B49)\u63D0\u4F9B\u7684\u4E00\u4E2A\u6846\u67B6\uFF0C\u5B83\u4F9D\u9760 int \u503C\u6765\u8868\u793A\u72B6\u6001</p><p>Lock\uFF0CLock \u6BD4 synchronized \u5173\u952E\u5B57\u66F4\u7075\u6D3B\uFF0C\u800C\u4E14\u5728\u541E\u5410\u91CF\u5927\u7684\u65F6\u5019\u6548\u7387\u66F4\u9AD8\uFF0C\u6839\u636E JSR-133 \u7684\u5B9A\u4E49\uFF0C\u5B83 happens-before \u7684\u8BED\u4E49\u548C synchronized \u5173\u952E\u5B57\u6548\u679C\u662F\u4E00\u6A21\u4E00\u6837\u7684\uFF0C\u5B83\u552F\u4E00\u7684\u7F3A\u70B9\u4F3C\u4E4E\u662F\u7F3A\u4E4F\u4E86\u4ECE lock \u5230 finally \u5757\u4E2D unlock \u8FD9\u6837\u5BB9\u6613\u9057\u6F0F\u7684\u56FA\u5B9A\u4F7F\u7528\u642D\u914D\u7684\u7EA6\u675F\uFF0C\u9664\u4E86 lock \u548C unlock \u65B9\u6CD5\u4EE5\u5916\uFF0C\u8FD8\u6709\u8FD9\u6837\u4E24\u4E2A\u503C\u5F97\u6CE8\u610F\u7684\u65B9\u6CD5\uFF1A</p><p>lockInterruptibly\uFF1A\u5982\u679C\u5F53\u524D\u7EBF\u7A0B\u6CA1\u6709\u88AB\u4E2D\u65AD\uFF0C\u5C31\u83B7\u53D6\u9501\uFF1B\u5426\u5219\u629B\u51FA InterruptedException\uFF0C\u5E76\u4E14\u6E05\u9664\u4E2D\u65AD</p><p>tryLock\uFF0C\u53EA\u5728\u9501\u7A7A\u95F2\u7684\u65F6\u5019\u624D\u83B7\u53D6\u8FD9\u4E2A\u9501\uFF0C\u5426\u5219\u8FD4\u56DE false\uFF0C\u6240\u4EE5\u5B83\u4E0D\u4F1A block \u4EE3\u7801\u7684\u6267\u884C</p><p>ReadWriteLock\uFF0C\u8BFB\u5199\u9501\uFF0C\u8BFB\u5199\u5206\u5F00\uFF0C\u8BFB\u9501\u662F\u5171\u4EAB\u9501\uFF0C\u5199\u9501\u662F\u72EC\u5360\u9501\uFF1B\u5BF9\u4E8E\u8BFB-\u5199\u90FD\u8981\u4FDD\u8BC1\u4E25\u683C\u7684\u5B9E\u65F6\u6027\u548C\u540C\u6B65\u6027\u7684\u60C5\u51B5\uFF0C\u5E76\u4E14\u8BFB\u9891\u7387\u8FDC\u8FDC\u5927\u8FC7\u5199\uFF0C\u4F7F\u7528\u8BFB\u5199\u9501\u4F1A\u6BD4\u666E\u901A\u4E92\u65A5\u9501\u6709\u66F4\u597D\u7684\u6027\u80FD\u3002</p><p>ReentrantLock\uFF0C\u53EF\u91CD\u5165\u9501(lock \u884C\u4E3A\u53EF\u4EE5\u5D4C\u5957\uFF0C\u4F46\u662F\u9700\u8981\u548C unlock \u884C\u4E3A\u4E00\u4E00\u5BF9\u5E94)\uFF0C\u6709\u51E0\u70B9\u9700\u8981\u6CE8\u610F\uFF1A</p><p>\u6784\u9020\u5668\u652F\u6301\u4F20\u5165\u4E00\u4E2A\u8868\u793A\u662F\u5426\u662F\u516C\u5E73\u9501\u7684 boolean \u53C2\u6570\uFF0C\u516C\u5E73\u9501\u4FDD\u8BC1\u4E00\u4E2A\u963B\u585E\u7684\u7EBF\u7A0B\u6700\u7EC8\u80FD\u591F\u83B7\u5F97\u9501\uFF0C\u56E0\u4E3A\u662F\u6709\u5E8F\u7684\uFF0C\u6240\u4EE5\u603B\u662F\u53EF\u4EE5\u6309\u7167\u8BF7\u6C42\u7684\u987A\u5E8F\u83B7\u5F97\u9501\uFF1B\u4E0D\u516C\u5E73\u9501\u610F\u5473\u7740\u540E\u8BF7\u6C42\u9501\u7684\u7EBF\u7A0B\u53EF\u80FD\u5728\u5176\u524D\u9762\u6392\u5217\u7684\u4F11\u7720\u7EBF\u7A0B\u6062\u590D\u524D\u62FF\u5230\u9501\uFF0C\u8FD9\u6837\u5C31\u6709\u53EF\u80FD\u63D0\u9AD8\u5E76\u53D1\u7684\u6027\u80FD</p><p>\u8FD8\u63D0\u4F9B\u4E86\u4E00\u4E9B\u76D1\u89C6\u9501\u72B6\u6001\u7684\u65B9\u6CD5\uFF0C\u6BD4\u5982 <code>isFair</code>\u3001<code>isLocked</code>\u3001<code>hasWaiters</code>\u3001<code>getQueueLength</code> \u7B49\u7B49</p><p><code>ReentrantReadWriteLock</code>\uFF0C\u53EF\u91CD\u5165\u8BFB\u5199\u9501</p><p><code>Condition</code>\uFF0C\u4F7F\u7528\u9501\u7684 newCondition \u65B9\u6CD5\u53EF\u4EE5\u8FD4\u56DE\u4E00\u4E2A\u8BE5\u9501\u7684 Condition \u5BF9\u8C61\uFF0C\u5982\u679C\u8BF4\u9501\u5BF9\u8C61\u662F\u53D6\u4EE3\u548C\u589E\u5F3A\u4E86 synchronized \u5173\u952E\u5B57\u7684\u529F\u80FD\u7684\u8BDD\uFF0C\u90A3\u4E48 Condition \u5219\u662F\u5BF9\u8C61 wait/notify/notifyAll \u65B9\u6CD5\u7684\u66FF\u4EE3\u3002\u5728\u4E0B\u9762\u8FD9\u4E2A\u4F8B\u5B50\u4E2D\uFF0Clock \u751F\u6210\u4E86\u4E24\u4E2A condition\uFF0C\u4E00\u4E2A\u8868\u793A\u4E0D\u6EE1\uFF0C\u4E00\u4E2A\u8868\u793A\u4E0D\u7A7A\uFF1A</p>',35);function a(i,d){return r}var l=o(c,[["render",a],["__file","common-concurrency-classes-under-java-8-concurrent-packages.html.vue"]]);export{l as default};
