import{b as e}from"./app.9da19d67.js";import{_ as a}from"./plugin-vue_export-helper.21dcd24c.js";const r={},p=e('<h2 id="\u5728\u4F60\u7684\u4E1A\u4F59\u65F6\u95F4-\u8BF7\u4E0D\u8981\u63A5\u53D7\u4E2A\u4EBA\u5DE5\u4F5C-\u4F46\u8981\u63D0\u9AD8\u81EA\u5DF1" tabindex="-1"><a class="header-anchor" href="#\u5728\u4F60\u7684\u4E1A\u4F59\u65F6\u95F4-\u8BF7\u4E0D\u8981\u63A5\u53D7\u4E2A\u4EBA\u5DE5\u4F5C-\u4F46\u8981\u63D0\u9AD8\u81EA\u5DF1" aria-hidden="true">#</a> \u5728\u4F60\u7684\u4E1A\u4F59\u65F6\u95F4\uFF0C\u8BF7\u4E0D\u8981\u63A5\u53D7\u4E2A\u4EBA\u5DE5\u4F5C\uFF0C\u4F46\u8981\u63D0\u9AD8\u81EA\u5DF1</h2><p>\u8FD9\u4E2A\u8BDD\u9898\u5F88\u7EA0\u7ED3</p><p>\u73B0\u5728\u793E\u4F1A\uFF0C\u6709\u5F88\u591A\u4EBA\u90FD\u5728\u5229\u7528\u4E2A\u4EBA\u65F6\u95F4\u517C\u804C\u8D5A\u94B1\uFF0C\u7A0B\u5E8F\u5458\u4FD7\u79F0\u201C\u63A5\u79C1\u6D3B\u201D\uFF0C\u5176\u4ED6\u884C\u4E1A\u79F0\u4F5C\u517C\u804C\uFF0C\u6BD4\u5982\u4E0B\u4E86\u73ED\u51FA\u53BB\u8DD1\u6EF4\u6EF4\uFF0C\u5468\u672B\u517C\u804C\u62A2\u5355\u9001\u5916\u5356\u7B49\u7B49\uFF0C\u90FD\u662F\u666E\u901A\u4EBA\u5F88\u5E38\u89C1\u7684\u517C\u804C\u65B9\u5F0F\u3002</p><p>\u751A\u81F3\u5F88\u591A\u6280\u672F\u540C\u884C\uFF0C\u6211\u542C\u8BF4\u4E5F\u6709\u5468\u672B\u53BB\u8DD1\u6EF4\u6EF4\u548C\u9001\u5916\u5356\u7684\uFF0C\u6211\u89C9\u5F97\u5F88\u4E0D\u53EF\u601D\u8BAE\uFF0C\u7136\u800C\u6211\u7684\u89C2\u70B9\u662F\u5982\u679C\u4F60\u60F3\u6210\u4E3A\u6210\u529F\u4EBA\u58EB\uFF0C\u9AD8\u6536\u5165\u4EBA\u7FA4\u90A3\u4E48\u4F60\u4E0D\u5E94\u8BE5\u53BB\u505A\u517C\u804C\uFF0C\u4E0D\u8981\u628A\u4F60\u5B9D\u8D35\u7684\u65F6\u95F4\uFF0C\u6D6A\u8D39\u5728\u517C\u804C\u4E0A\u3002</p><h2 id="\u7B80\u5355\u7ECF\u6D4E\u5B66\u5206\u6790" tabindex="-1"><a class="header-anchor" href="#\u7B80\u5355\u7ECF\u6D4E\u5B66\u5206\u6790" aria-hidden="true">#</a> \u7B80\u5355\u7ECF\u6D4E\u5B66\u5206\u6790</h2><p>\u6D89\u53CA\u5230\u6536\u5165\uFF0C\u6587\u5B57\u6D17\u8111\u663E\u5F97\u5F88\u7A7A\u6D1E\u4E86\u5C31\uFF0C\u90A3\u4E48\u4ECA\u5929\u6211\u7ED9\u4F60\u6765\u4E00\u4E2A\u7B80\u5355\u7684\u7ECF\u6D4E\u5B66\u8BBA\u8C03\u3002</p><p>\u6211\u4EEC\u770B\u4E00\u4E2A\u6848\u4F8B\uFF0C\u674E\u5609\u8BDA\u662F\u9999\u6E2F\u9996\u5BCC\uFF0C\u4F46\u662F\u674E\u5609\u8BDA\u4F1A\u4EB2\u81EA\u4FEE\u526A\u81EA\u5BB6\u7684\u8349\u576A\u5417\uFF1F\u9664\u975E\u67D0\u4E00\u5929\u4ED6\u60F3\u4F53\u9A8C\u4E00\u4E0B\u52B3\u52A8\u4E50\u8DA3\u4EC0\u4E48\u7684\uFF0C\u5426\u5219\u4E00\u5B9A\u662F\u8BF7\u4EBA\u6765\u4FEE\u526A\u8349\u576A\u3002</p><p>\u674E\u5609\u8BDA\u8BF7\u4EBA\u4FEE\u526A\u8349\u576A\u662F\u56E0\u4E3A\u4ED6\u6709\u94B1\u5417\uFF1F\u4E0D\u5168\u662F\uFF0C\u54EA\u6015\u674E\u5609\u8BDA\u6CA1\u94B1\uFF0C\u4ED6\u4E5F\u4E0D\u4F1A\u4EB2\u81EA\u4FEE\u526A\u8349\u576A\uFF0C\u8FD9\u91CC\u9762\u5C31\u8981\u6D89\u53CA\u7ECF\u6D4E\u5B66\u7684\u6BD4\u8F83\u4F18\u52BF\u4E86\u3002</p><p>\u5047\u8BBE\u8BF7\u6765\u7684\u674E\u5927\u53D4\u82B1\u8D393\u4E2A\u5C0F\u65F6\u5C31\u80FD\u4FEE\u7406\u5B8C\u8349\u576A\uFF0C\u800C\u674E\u5609\u8BDA2\u4E2A\u5C0F\u65F6\u5C31\u80FD\u4FEE\u7406\u5B8C\u8349\u576A\uFF0C\u56E0\u4E3A\u674E\u5609\u8BDA\u806A\u660E\u53C8\u80FD\u5E72\uFF0C\u4E0D\u7136\u4E0D\u4F1A\u6D3B\u523090\u5C81\u4E86\uFF0C\u8FD8\u4E0D\u9000\u4F11\uFF0C\u90A3\u4E48\u674E\u5609\u8BDA\u8FD9\u4E48\u725B\u9F3B\u5E94\u8BE5\u505A\u9999\u6E2F\u7B2C\u4E00\u7684\u4FEE\u8349\u5DE5\u5417\uFF1F</p><p>\u90A3\u80AF\u5B9A\u4E0D\u5E94\u8BE5\uFF0C\u56E0\u4E3A\u674E\u5609\u8BDA\u505A\u81EA\u5DF1\u7684\u4E8B\u60C5\uFF0C\u4E00\u5C0F\u65F6\u53EF\u4EE5\u8D5A100\u4E07\u6E2F\u5E01\uFF0C\u800C\u674E\u5927\u53D4\u4E0D\u4FEE\u8349\u576A\uFF0C\u53BB\u505A\u5176\u4ED6\u4E8B\u60C5\u7684\u8BDD\uFF0C\u4E00\u5C0F\u65F6\u53EA\u80FD\u8D5A20\u6E2F\u5E01\uFF0C\u90A3\u4E48\u5E2E\u674E\u5609\u8BDA\u4FEE\u8349\u576A\u7684\u4EF7\u683C\u53EA\u8981\u9AD8\u4E8E20\u6E2F\u5E01\uFF0C\u4F4E\u4E8E100\u4E07\u6E2F\u5E01\u6BCF\u5C0F\u65F6\uFF0C\u674E\u5609\u8BDA\u5C31\u4E0D\u5E94\u8BE5\u81EA\u5DF1\u4FEE\u8349\u576A\uFF0C\u800C\u5E94\u8BE5\u82B1\u94B1\u8BF7\u674E\u5927\u53D4\u6765\u505A\u4FEE\u3002</p><p>\u8FD9\u5C31\u662F18\u4E16\u7EAA\u7ECF\u6D4E\u5B66\u5BB6\u674E\u5609\u56FE\u63D0\u51FA\u7684\u6BD4\u8F83\u4F18\u52BF\u7406\u8BBA\uFF0C\u5199\u5728\u4E86\u5927\u5B66\u7684\u6BCF\u4E00\u4E2A\u7ECF\u6D4E\u5B66\u8BFE\u672C\u91CC\uFF0C\u8D38\u6613\u7684\u57FA\u7840\u5E76\u4E0D\u662F\u7EDD\u5BF9\u4F18\u52BF\uFF0C\u800C\u662F\u6BD4\u8F83\u4F18\u52BF\u3002\u5982\u679C\u6709\u7EDD\u5BF9\u4F18\u52BF\u90A3\u5C31\u53EB\u5784\u65AD\uFF0C\u4E0D\u5B58\u5728\u6BD4\u8F83\u4E86\u3002</p><p>\u6BD4\u8F83\u4F18\u52BF\u7684\u6838\u5FC3\u7406\u8BBA\u5C31\u662F\uFF0C\u5355\u72EC\u4E00\u4E2A\u4EBA\u6216\u8005\u5355\u72EC\u4E00\u4E2A\u56FD\u5BB6\u673A\u6784\uFF0C\u5176\u7CBE\u529B\u548C\u673A\u4F1A\u6210\u672C\u90FD\u662F\u6709\u9650\u7684\uFF0C\u54EA\u6015\u4F60\u5404\u65B9\u9762\u90FD\u662F\u5168\u7403\u7B2C\u4E00\u7684\u4EBA\u624D\uFF0C\u6700\u806A\u660E\u7684\u505A\u6CD5\u4E5F\u4E0D\u662F\u56DB\u5904\u51FA\u51FB\uFF0C\u4E8B\u5FC5\u8EAC\u4EB2\uFF0C\u800C\u662F\u628A\u6709\u9650\u7684\u65F6\u95F4\u3001\u7CBE\u529B\u548C\u8D44\u6E90\u653E\u5728\u81EA\u5DF1\u6700\u64C5\u957F\u7684\u5730\u65B9\uFF0C\u6362\u53E5\u8BDD\u8BF4\u5C31\u662F\u6700\u8D5A\u94B1\u7684\u5730\u65B9\u3002</p><p>\u90A3\u4E48\u597D\uFF0C\u6D89\u53CA\u5230\u6211\u4EEC\u666E\u901A\u4EBA\uFF0C\u5176\u5B9E\u4E5F\u662F\u4E00\u6837\uFF0C\u4F60\u7684\u7CBE\u529B\u548C\u65F6\u95F4\u5305\u62EC\u77E5\u8BC6\u5F88\u6709\u9650\uFF0C\u8FD9\u90FD\u662F\u4F60\u4EC5\u6709\u7684\u4E5F\u6700\u64C5\u957F\u7684\u8D5A\u94B1\u8D22\u5BCC\uFF0C\u505A\u517C\u804C\u4E5F\u662F\u8FD9\u4E2A\u9053\u7406\uFF0C\u5982\u679C\u4F60\u505A\u81EA\u5DF1\u7684\u672C\u804C\u5DE5\u4F5C\u4E00\u5C0F\u65F6\u80FD\u8D5A200\u5143\uFF0C\u517C\u804C\u8DD1\u6EF4\u6EF4\u4E00\u5C0F\u65F6\u80FD\u8D5A30\u5143\uFF0C\u9001\u5916\u5356\u4E00\u5C0F\u65F6\u80FD\u8D5A15\u5143\uFF0C\u4F60\u7684\u6700\u4F73\u9009\u62E9\u662F\u4EC0\u4E48?\u4E0D\u50BB\u7684\u5E94\u8BE5\u90FD\u4F1A\u9009\u62E9\u5427\uFF1F\u4E0D\u662F\u53BB\u8DD1\u6EF4\u6EF4\uFF0C\u800C\u662F\u96C6\u4E2D\u7CBE\u529B\u53BB\u505A\u597D\u81EA\u5DF1\u7684\u672C\u804C\u5DE5\u4F5C\u3002</p><h3 id="\u793E\u4F1A\u5206\u5DE5" tabindex="-1"><a class="header-anchor" href="#\u793E\u4F1A\u5206\u5DE5" aria-hidden="true">#</a> \u793E\u4F1A\u5206\u5DE5</h3><p>\u9A6C\u5316\u817E\u3001\u674E\u5609\u8BDA\u3001\u9A6C\u4E91\u8FD9\u4E48\u725B\u903C\uFF0C\u8FD9\u4E48\u806A\u660E\u4E3A\u4F55\u4ED6\u4EEC\u4E0D\u628A\u6240\u6709\u7684\u94B1\u90FD\u6323\u4E86\u5462\uFF1F\u8BF7\u51E0\u4E07\u51E0\u5341\u4E07\u7684\u5458\u5DE5\u6765\u5E72\u6D3B\u53D1\u5DE5\u8D44\u5E72\u4EC0\u4E48\uFF1F</p><p>\u56E0\u4E3A\u4ED6\u4EEC\u4E00\u5929\u53EA\u670924\u4E2A\u5C0F\u65F6\u554A\uFF0C\u80FD\u529B\u518D\u5F3A\uFF0C\u4F60\u4E5F\u53EA\u80FD\u5DE5\u4F5C20\u5C0F\u65F6\uFF0C4\u4E2A\u5C0F\u65F6\u5403\u996D\u7761\u89C9\u5427\uFF1F\u6240\u4EE5\u4ED6\u4EEC\u628A\u81EA\u5DF1\u6BCF\u5929\u6709\u9650\u7684\u65F6\u95F4\u96C6\u4E2D\u5728\u81EA\u5DF1\u6700\u80FD\u8D5A\u94B1\u7684\u9886\u57DF\uFF0C\u7136\u540E\u8BF7\u522B\u4EBA\u6765\u505A\u5176\u4ED6\u4ED6\u4E0D\u64C5\u957F\u6216\u8005\u538B\u6839\u4E0D\u503C\u5F97\u505A\u7684\u4E8B\u60C5\uFF0C\u8FD9\u4E00\u8FC7\u7A0B\uFF0C\u53EB\u793E\u4F1A\u5206\u5DE5\u3002</p><p>\u793E\u4F1A\u5206\u5DE5\u662F\u5E02\u573A\u7ECF\u6D4E\u6761\u4EF6\u4E0B\uFF0C\u6574\u4E2A\u793E\u4F1A\u6240\u6709\u4EBA\u90FD\u5145\u5206\u53D1\u6325\u81EA\u5DF1\u52B3\u52A8\u529B\u7684\u6700\u4F18\u9009\u62E9\uFF0C\u4E5F\u662F\u6700\u7406\u60F3\u3001\u6700\u4EBA\u6027\u5316\u7684\u793E\u4F1A\u73B0\u8C61\uFF0C\u6BCF\u4E00\u4E2A\u4EBA\u5E94\u8BE5\u53EA\u4ECE\u4E8B\u4E00\u4EFD\u5DE5\u4F5C\uFF0C\u800C\u4E14\u8FD9\u4E2A\u5DE5\u4F5C\u4F1A\u7EC6\u5206\u5230\u6781\u81F4\uFF0C\u5206\u5DE5\u5230\u6781\u81F4\u7684\u793E\u4F1A\uFF0C\u4F1A\u8BA9\u4E00\u4E2A\u4EBA\u4E13\u95E8\u8D1F\u8D23\u505A\u5176\u4E2D\u7684\u4E00\u4EF6\u4E8B\u548C\u4E00\u79CD\u4E8B\uFF0C\u8FD9\u5C31\u662F\u4FD7\u8BED360\u884C\uFF0C360\u884C\u5C31\u662F\u8FD9\u4E48\u6765\u7684\u3002</p><h2 id="\u4E00\u3001\u4E3A\u4EC0\u4E48\u8981\u505A\u79C1\u6D3B" tabindex="-1"><a class="header-anchor" href="#\u4E00\u3001\u4E3A\u4EC0\u4E48\u8981\u505A\u79C1\u6D3B" aria-hidden="true">#</a> \u4E00\u3001\u4E3A\u4EC0\u4E48\u8981\u505A\u79C1\u6D3B\uFF1F</h2><p><strong>\u6211\u53EF\u4EE5\u6BEB\u4E0D\u907F\u8BB3\u7684\u8BF4\uFF1A\u5C31\u662F\u60F3\u8D5A\u94B1</strong>\u3002\u8BA8\u8BBA\u8D5A\u94B1\u8FD9\u4E2A\u4E8B\u6CA1\u4EC0\u4E48\u4E0D\u597D\u610F\u601D\u7684\uFF0C\u6709\u6B32\u671B\u60F3\u8981\u8D5A\u66F4\u591A\u7684\u94B1\u66F4\u6CA1\u6709\u4EC0\u4E48\u597D\u7F9E\u803B\u7684\u3002\u5927\u5BB6\u90FD\u77E5\u9053\uFF0C\u7A0B\u5E8F\u5458\u662F\u4E00\u4E2A\u8D77\u85AA\u9AD8\u3001\u4F46\u4E2A\u4EBA\u53D1\u5C55\u5DEE\u5F02\u660E\u663E\u3001\u4E2D\u5E74\u5371\u673A\u611F\u4E25\u91CD\u7684\u4E00\u4E2A\u804C\u4E1A\u300235\u5C81\u4E4B\u540E\u4F55\u53BB\u4F55\u4ECE\uFF0C\u662F\u5426\u4F1A\u88AB\u88C1\u5458\uFF0C\u662F\u5F88\u591A\u4E2D\u5E74\u4EBA\u7126\u8651\u611F\u7684\u6765\u6E90\u3002\u800C\u4E2D\u5E74\uFF0C\u53C8\u662F\u4E00\u4E2A\u9762\u4E34\u5404\u65B9\u9762\u538B\u529B\u7684\u65F6\u671F\uFF0C\u6240\u4EE5\u5C3D\u65E9\u7684\u79EF\u7D2F\u8D22\u5BCC+\u589E\u52A0\u88AB\u52A8\u6536\u5165\u662F\u4E00\u4E2A\u4E0D\u5BB9\u5FFD\u89C6\u7684\u95EE\u9898\u3002\u6211\u6BD5\u4E1A\u7684\u65F6\u5019\u4E00\u76F4\u5F88\u6709\u7126\u8651\u611F\uFF0C\u6EE1\u8111\u5B50\u5C31\u60F3\u7740\u8D5A\u94B1\u4E70\u623F\uFF0C\u89E3\u51B3\u57FA\u672C\u7684\u7ECF\u6D4E\u95EE\u9898\uFF0C\u6240\u4EE5\u8DF3\u69FD\u591A\uFF08\u5E76\u4E0D\u662F\u5F88\u53EF\u53D6\uFF09+\u75AF\u72C2\u71AC\u591C\u505A\u79C1\u6D3B\uFF0C\u7136\u540E\u987A\u5229\u7684\u529E\u4E86\u5DE5\u4F5C\u5C45\u4F4F\u8BC1\u4E70\u4E86\u623F\u3002\u6240\u4EE5\u73B0\u5728\u5728\u5DE5\u4F5C\u7684\u65F6\u5019\uFF0C\u80FD\u591F\u7126\u8651\u611F\u4E0D\u90A3\u4E48\u4E25\u91CD\u7684\u53BB\u4E13\u5FC3\u5DE5\u4F5C\uFF0C\u5168\u529B\u4EE5\u8D74\u7684\u53BB\u5DE5\u4F5C\uFF0C\u505A\u81EA\u5DF1\u771F\u6B63\u60F3\u505A\u7684\u4E8B\u3002</p><p>\u9664\u4E86\u8D5A\u94B1\u4EE5\u5916\uFF0C\u6211\u66F4\u60F3\u901A\u8FC7\u79C1\u6D3B\u903C\u7740\u81EA\u5DF1\u63D0\u5347\u81EA\u5DF1\u7684\u6280\u672F\u80FD\u529B\u3002\u6211\u5728\u9009\u62E9\u79C1\u6D3B\u7684\u65F6\u5019\uFF0C\u90FD\u662F\u8DDF\u5DE5\u4F5C\u65B9\u5411\u662F\u4E00\u81F4\u7684\uFF0C\u8FD9\u6837\u80FD\u4FDD\u8BC1\u81EA\u5DF1\u5728\u4E0B\u73ED\u65F6\u95F4\uFF0C\u4E0D\u60F3\u5B66\u4E60\u7684\u65F6\u5019\u4E5F\u80FD\u75AF\u72C2push\u81EA\u5DF1\u53BB\u5B8C\u6210\u8DDF\u7532\u65B9\u7B7E\u597D\u5408\u540C\uFF0C\u5FC5\u987B\u505A\u7684\u6D3B\uFF0C\u65E0\u5F62\u4E4B\u4E2D\u4FDD\u8BC1\u4E86\u81EA\u5DF1\u7684\u5B66\u4E60\u548C\u7EC3\u4E60\u65F6\u95F4\u3002</p><p>\u5176\u5B9E\u9664\u4E86\u8D5A\u94B1\u548C\u903C\u7740\u81EA\u5DF1\u63D0\u5347\u6280\u672F\u80FD\u529B\u4EE5\u5916\uFF0C\u8FD8\u6709\u4E00\u4E2A\u66F4\u957F\u671F\u7684\u6536\u76CA\uFF1A\u4EBA\u8109\u3002\u505A\u79C1\u6D3B\u4F1A\u6781\u5927\u7684\u6269\u5C55\u4F60\u7684\u5708\u5B50\uFF0C\u7532\u65B9\u4E59\u65B9\u3001\u5FD7\u540C\u9053\u5408\u7684\u4EBA\u3001\u8D44\u6DF1\u5927\u725B\u7B49\uFF0C\u5BF9\u4F60\u672A\u6765\u7684\u804C\u4E1A\u53D1\u5C55\u9053\u8DEF\u90FD\u6709\u4E0D\u5C11\u5E2E\u52A9\uFF0C\u6211\u4EE5\u524D\u4F1A\u505A\u4E09\u5E74\u804C\u4E1A\u89C4\u5212\uFF0C\u4F46\u662F\u540E\u6765\u8BA4\u8BC6\u7684\u4EBA\u591A\u4E86\uFF0C\u53D1\u73B0\u6839\u672C\u9884\u6D4B\u4E0D\u4E86\u4E09\u5E74\u540E\u7684\u804C\u4E1A\u53D1\u5C55\uFF0C\u53EA\u80FD\u9884\u6D4B\u4E00\u5E74\u7684\u5C31\u4E0D\u9519\u4E86\uFF0C\u4E09\u5E74\u540E\u7684\u53D1\u5C55\u6BD4\u81EA\u5DF1\u9884\u60F3\u7684\u8981\u597D\u591A\u4E86\u3002\u6240\u4EE5\u4E5F\u5F88\u611F\u8C22\u4E4B\u524D\u505A\u79C1\u6D3B\u7684\u8FD9\u6BB5\u7ECF\u5386\u3002</p><h2 id="\u4E8C\u3001\u79C1\u6D3B\u7684\u539F\u5219" tabindex="-1"><a class="header-anchor" href="#\u4E8C\u3001\u79C1\u6D3B\u7684\u539F\u5219" aria-hidden="true">#</a> \u4E8C\u3001\u79C1\u6D3B\u7684\u539F\u5219</h2><p>\u79C1\u6D3B\u7684\u79CD\u7C7B\u592A\u591A\u4E86\uFF0C\u9009\u62E9\u7684\u6210\u672C\u4E5F\u662F\u4E0D\u53EF\u5FFD\u89C6\u7684\uFF0C\u9009\u4E86A\uFF0C\u5C31\u6CA1\u6709\u65F6\u95F4\u505AB\uFF0CA\u548CB\u54EA\u4E2A\u66F4\u597D\uFF1F\u867D\u7136\u4E0D\u80FD\u6709100%\u7684\u5224\u65AD\u6807\u51C6\uFF0C\u4F46\u662F\u6839\u636E\u6211\u7684\u7ECF\u9A8C\uFF0C\u603B\u7ED3\u4E86\u4E09\u6761\u5173\u4E8E\u63A5\u79C1\u6D3B\u7684\u539F\u5219\uFF0C\u5E0C\u671B\u5BF9\u5927\u5BB6\u6709\u6240\u5E2E\u52A9\uFF1A</p><ol><li>\u672C\u804C\u5DE5\u4F5C\u6C38\u8FDC\u662F\u7B2C\u4E00\u4F4D\u7684\uFF1A\u5982\u679C\u516C\u53F8\u4E0D\u5141\u8BB8\u3001\u6216\u8005\u6240\u5728\u7684\u56E2\u961F\u9ED8\u8BA4\u4E0D\u5141\u8BB8\uFF0C\u653E\u5F03\u5427\u3002\u521A\u5F00\u59CB\u505A\u79C1\u6D3B\u8D5A\u7684\u90A3\u70B9\u94B1\uFF0C\u8FDC\u4E0D\u53CA\u672C\u804C\u5DE5\u4F5C\u5E26\u6765\u7684\u957F\u671F\u6536\u76CA\u5927\u3002</li><li>\u5145\u5206\u8003\u8651\u81EA\u5DF1\u7684\u6C89\u6CA1\u6210\u672C\uFF1A\u65F6\u85AA\u6700\u4F4E\u4E0D\u4F4E\u4E8E\u4F60\u73B0\u5728\u5DE5\u4F5C\u65F6\u85AA\u3002\u6211\u7684\u539F\u5219\u662F\u8861\u91CF\u597D\u96BE\u5EA6+\u4EBA\u529B\u6210\u672C\u540E\uFF0C\u518D\u81F3\u5C11\u4E58\u4EE52\uFF0C\u62A5\u4EA4\u4ED8\u65F6\u95F4\u548C\u62A5\u916C\uFF0C\u7136\u540E\u518D\u534F\u5546\u3002\u8FD9\u6837\u80FD\u4FDD\u8BC1\u81EA\u5DF1\u80FD\u591F\u53CA\u65F6\u4EA4\u4ED8\uFF0C\u4E5F\u4E0D\u81F3\u4E8E\u505A\u7684\u65F6\u5019\u5F88\u52C9\u5F3A\u3002</li><li>\u505A\u957F\u671F\u6536\u76CA\u7684\u4E8B\uFF1A\u5207\u5FCC\u6361\u829D\u9EBB\u4E22\u897F\u74DC\uFF0C\u6700\u597D\u4E0E\u81EA\u5DF1\u7684\u5DE5\u4F5C\u5185\u5BB9\u5F3A\u76F8\u5173\u3002\u517C\u804C\u6EF4\u6EF4\u53F8\u673A\u3001\u4F4E\u4EF7\u683C\u7684\u5BB6\u6559\u3001\u9910\u9986\u670D\u52A1\u5458\u7B49\u65F6\u95F4\u548C\u6C47\u62A5\u4E0D\u6210\u6B63\u6BD4\u7684\u5DE5\u4F5C\uFF0C\u4E0D\u5728\u6211\u8BF4\u7684\u8303\u56F4\u5185\uFF08\u5F53\u7136\u6CA1\u6709\u4EFB\u4F55\u5176\u5B9E\u4EE5\u4E0A\u5404\u79CD\u804C\u4F4D\u7684\u610F\u601D\uFF0C\u53EA\u662F\u8868\u8FF0\u9009\u62E9\u5F88\u91CD\u8981\uFF09</li></ol><h2 id="\u4E09\u3001\u505A\u4EC0\u4E48\u79C1\u6D3B\u6BD4\u8F83\u597D" tabindex="-1"><a class="header-anchor" href="#\u4E09\u3001\u505A\u4EC0\u4E48\u79C1\u6D3B\u6BD4\u8F83\u597D" aria-hidden="true">#</a> \u4E09\u3001\u505A\u4EC0\u4E48\u79C1\u6D3B\u6BD4\u8F83\u597D\uFF1F</h2><p>\u73B0\u5728\u7684\u73AF\u5883\u548C\u51E0\u5E74\u524D\u5DF2\u7ECF\u5F88\u4E0D\u4E00\u6837\u4E86\uFF0C\u4EE5\u524D\u7A0B\u5E8F\u5458\u5E38\u89C1\u7684\u79C1\u6D3B\u6709\u4E0D\u5C11\u662F\u732A\u516B\u6212\u7B49\u5916\u5305\u7F51\u7AD9\uFF0C\u6211\u5176\u5B9E\u4E0D\u662F\u5F88\u63A8\u8350\uFF0C\u81EA\u5DF1\u4E5F\u4ECE\u6765\u6CA1\u6709\u5728\u8FD9\u4E9B\u7F51\u7AD9\u4E0A\u6CE8\u518C\u8FC7\uFF0C\u56E0\u4E3A\u524D\u671F\u4ED8\u51FA\u7684\u82B1\u65F6\u95F4\u6311\u9009\u5339\u914D\u7684\u6210\u672C\u592A\u9AD8\u3002\u4E0D\u7B26\u5408\u6211\u7684\u7B2C\u4E8C\u4E2A\u539F\u5219\u3002\u6211\u8981\u63A8\u8350\u7684\u6709\u597D\u51E0\u7C7B\uFF0C\u7BC7\u5E45\u539F\u56E0\uFF0C\u4ECA\u5929\u5148\u8BF4\u63A8\u8350\u7684\u7B2C\u4E00\u7C7B\u2014\u2014<strong>\u6253\u6BD4\u8D5B/\u505A\u6A21\u578B</strong></p><p>\u6700\u540E\u9700\u8981\u63D0\u9192\u7684\u4E00\u70B9\u662F\uFF1A\u6C38\u8FDC\u6C38\u8FDC\u4E0D\u8981\u7528\u516C\u53F8\u7684\u7535\u8111\u3001\u8D44\u6E90\u3001\u5DE5\u4F5C\u65F6\u95F4\u6765\u53BB\u6253\u6BD4\u8D5B\uFF01\u6CA1\u6709\u67E5\u51FA\u6765\u7684\uFF0C\u53EA\u662F\u516C\u53F8\u4E0D\u60F3\u67E5\u4F60\uFF0C\u5B9E\u9645\u4E0A\u4F60\u7684\u6240\u6709\u64CD\u4F5C\u90FD\u662F\u6709\u8BB0\u5F55\u7684\uFF0C\u8C28\u614E\u4E3A\u597D\uFF0C\u4E0D\u8981\u5FD8\u4E86\u524D\u9762\u8BF4\u7684\u7B2C\u4E09\u4E2A\u539F\u5219\uFF0C\u4E0D\u8981\u6361\u4E86\u829D\u9EBB\u4E22\u897F\u74DC\uFF0C\u975E\u5E38\u5F97\u4E0D\u507F\u5931\u3002\u4E00\u822C\u6253\u6BD4\u8D5B\uFF0C\u4E3B\u529E\u65B9\u4F1A\u63D0\u4F9B\u5145\u8DB3\u7684\u8BA1\u7B97\u8D44\u6E90\uFF0C\u5982\u679C\u4E0D\u63D0\u4F9B\uFF0C\u90A3\uFF0C\u53EF\u4EE5\u660E\u5E74\u4E0D\u8003\u8651\u53C2\u52A0\u8FD9\u5BB6\u4E86\uFF0C\u6362\u5BB6\u5427\uFF0C\u4E0B\u4E00\u4E2A\u66F4\u9999~</p><p>\u5199\u5230\u8FD9\u91CC\uFF0C\u53EF\u80FD\u6709\u4E9B\u670B\u53CB\u4EEC\u89C9\u5F97\uFF0C\u4F60\u8BF4\u7684\u8FD9\u4E2A\u96BE\u5EA6\u597D\u9AD8\u554A\uFF0C\u6211\u4E0D\u4F1A\u600E\u4E48\u529E\uFF1F\u6709\u6CA1\u6709\u53EF\u4EE5\u7EC3\u4E60\u7684\u5730\u65B9\uFF0C\u6211\u79EF\u7D2F\u79EF\u7D2F\u518D\u53BB\u6253\u6BD4\u8D5B\uFF1F\u81EA\u5B66\u5199\u4EE3\u7801\u561B\uFF1F\u8FD8\u662F\u81EA\u5B66\u53BB\u590D\u73B0\uFF1F\u4E00\u4E2A\u4EBA\u611F\u89C9\u5F88\u6CA1\u6709\u52A8\u529B\u53BB\u505A\uFF0C\u603B\u662F\u575A\u6301\u4E0D\u4E0B\u6765\u600E\u4E48\u529E\uFF1F</p><p>contribute</p>',29);function i(h,t){return p}var d=a(r,[["render",i],["__file","in-your-spare-time-please-don-not-take-personal-work-but-improve-yourself.html.vue"]]);export{d as default};
