import{b as e}from"./app.9da19d67.js";import{_ as o}from"./plugin-vue_export-helper.21dcd24c.js";const d={},t=e('<h2 id="mysql\u7279\u6027" tabindex="-1"><a class="header-anchor" href="#mysql\u7279\u6027" aria-hidden="true">#</a> mysql\u7279\u6027</h2><p>\u539F\u5B50\u6027\uFF1A\u4E8B\u52A1\u662F\u4E00\u4E2A\u4E0D\u53EF\u5206\u5272\u7684\u5DE5\u4F5C\u5355\u4F4D\uFF0C\u4E8B\u52A1\u4E2D\u7684\u64CD\u4F5C\u8981\u4E48\u90FD\u6210\u529F\uFF08commit\uFF09\uFF0C\u8981\u4E48\u90FD\u5931\u8D25\uFF08rollback\uFF09\u3002</p><p>\u4E00\u81F4\u6027\uFF1A\u4E8B\u52A1\u524D\u540E\u6570\u636E\u7684\u5B8C\u6574\u6027\u5FC5\u987B\u4FDD\u6301\u4E00\u81F4\u3002 \u4E8B\u52A1\u6267\u884C\u7684\u7ED3\u679C\u5FC5\u987B\u4F7F\u6570\u636E\u5E93\u4ECE\u4E00\u4E2A\u4E00\u81F4\u6027\u72B6\u6001\u53D8\u5230\u53E6\u4E00\u4E2A\u4E00\u81F4\u6027\u7684\u72B6\u6001\u3002\u5728\u591A\u4E2A\u4E8B\u52A1\u5E76\u884C\u60C5\u51B5\u4E0B\uFF1B</p><p>\u6301\u4E45\u6027\uFF1A\u6307\u4E00\u4E2A\u4E8B\u52A1\u4E00\u65E6\u88AB\u63D0\u4EA4\uFF0C\u5B83\u5BF9\u6570\u636E\u5E93\u4E2D\u6570\u636E\u7684\u6539\u53D8\u5C31\u662F\u6C38\u4E45\u6027\u7684\uFF0C\u63A5\u4E0B\u6765\u5373\u4F7F\u6570\u636E\u5E93\u53D1\u751F\u6545\u969C\u4E5F\u4E0D\u5E94\u8BE5\u5BF9\u5176\u6709\u4EFB\u4F55\u5F71\u54CD\u3002</p><p>\u9694\u79BB\u6027\uFF1A \u4E8B\u52A1\u7684\u9694\u79BB\u6027\u662F\u591A\u4E2A\u4E8B\u52A1\u5E76\u884C\uFF0C\u6BCF\u4E2A\u4E8B\u52A1\u4E0D\u80FD\u88AB\u5176\u4ED6\u4E8B\u52A1\u7684\u64CD\u4F5C\u6570\u636E\u6240\u5E72\u6270\uFF0C\u591A\u4E2A\u5E76\u53D1\u4E8B\u52A1\u4E4B\u95F4\u8981\u76F8\u4E92\u9694\u79BB\u3002</p><h2 id="mysql\u4E8B\u52A1\u9694\u79BB\u7EA7\u522B" tabindex="-1"><a class="header-anchor" href="#mysql\u4E8B\u52A1\u9694\u79BB\u7EA7\u522B" aria-hidden="true">#</a> mysql\u4E8B\u52A1\u9694\u79BB\u7EA7\u522B</h2><table><thead><tr><th style="text-align:center;">\u4E8B\u52A1\u9694\u79BB\u7EA7\u522B</th><th>\u6982\u5FF5</th></tr></thead><tbody><tr><td style="text-align:center;">read uncommitted(\u672A\u63D0\u4EA4\u8BFB)</td><td>\u5141\u8BB8\u5F53\u524D\u4E8B\u52A1\u8BFB\u53D6\u5230\u5176\u4ED6\u4E8B\u52A1\u672A\u63D0\u4EA4\u7684\u6570\u636E,\u4F1A\u51FA\u73B0\u810F\u8BFB\u3001\u4E0D\u53EF\u91CD\u590D\u8BFB\u3001\u5E7B\u8BFB</td></tr><tr><td style="text-align:center;">read committed(\u8BFB\u5DF2\u63D0\u4EA4)</td><td>\u5BF9\u4E8B\u52A1\u5DF2\u7ECF\u63D0\u4EA4\u7684\u6570\u636E\u8FDB\u884C\u8BFB\u53D6\uFF0C\u89E3\u51B3\u4E86\u810F\u8BFB\u3002\u4F1A\u51FA\u73B0\u5E7B\u8BFB\u548C\u4E0D\u53EF\u91CD\u590D\u8BFB</td></tr><tr><td style="text-align:center;">repeatable read(\u53EF\u91CD\u590D\u8BFB)</td><td>\u5728\u540C\u4E00\u4E2A\u4E8B\u52A1\u4E2D\uFF0C\u5BF9\u76F8\u540C\u67E5\u8BE2\u6761\u4EF6\uFF0C\u8BFB\u53D6\u6570\u636E\u90FD\u662F\u4E00\u81F4\u7684\u3002\u8BE5\u7EA7\u522B\u4F1A\u51FA\u73B0\u5E7B\u8BFB</td></tr><tr><td style="text-align:center;">serializable(\u4E32\u884C\u5316)</td><td>\u5B8C\u5168\u670D\u4ECE\u6570\u636E\u5E93\u7684ACID\uFF0C\u8BFB\u5199\u6570\u636E\u90FD\u4F1A\u9501\u4F4F\u5728\u4E8B\u52A1\u4E2D\u6D89\u53CA\u5230\u7684\u6570\u636E\u5E93\u8868\uFF0C\u5E76\u53D1\u6027\u80FD\u6781\u4F4E</td></tr></tbody></table><p>MySQL \u9ED8\u8BA4\u4F7F\u7528 RR \u7684\u4E8B\u52A1\u9694\u79BB\u7EA7\u522B\u3002</p><ul><li>\u810F\u8BFB\uFF1A\u6CA1\u6709\u63D0\u4EA4\u5C31\u53EF\u4EE5\u8BFB\u53D6\u5230\u6570\u636E\uFF1B</li><li>\u4E0D\u53EF\u91CD\u590D\u8BFB\uFF1A\u518D\u91CD\u590D\u8BFB\u4E00\u6B21\uFF0C\u6570\u636E\u4E0E\u4E0A\u6B21\u8BFB\u5230\u7684\u6570\u636E\u4E0D\u4E00\u6837\uFF1B</li><li>\u5E7B\u8BFB\uFF1A\u5728\u67E5\u8BE2\u67D0\u4E00\u6761\u4EF6\u7684\u6570\u636E\uFF0C\u5F00\u59CB\u67E5\u8BE2\u540E\uFF0C\u522B\u4EBA\u53C8\u52A0\u5165\u6216\u5220\u9664\u67D0\u4E9B\u6570\u636E\uFF0C\u518D\u8BFB\u53D6\u65F6\u4E0E\u539F\u6765\u7684\u6570\u636E\u4E0D\u4E00\u6837\u4E86\u3002</li></ul><p>\u4E0D\u53EF\u91CD\u590D\u8BFB\u5F62\u6210\u539F\u56E0\u5728\u4E8E\u6570\u636E\u7684\u4FEE\u6539\uFF1A\u5728\u540C\u4E00\u4E8B\u52A1\u4E2D\uFF0C\u540C\u6837\u7684\u6761\u4EF6\uFF0C\u7B2C\u4E00\u6B21\u8BFB\u7684\u6570\u636E\u548C\u7B2C\u4E8C\u6B21\u8BFB\u7684\u6570\u636E\u4E0D\u4E00\u6837\u3002\uFF08\u56E0\u4E3A\u4E2D\u95F4\u6709\u5176\u4ED6\u4E8B\u52A1\u63D0\u4EA4\u4E86\u4FEE\u6539\uFF09\u3002</p><p>\u5E7B\u8BFB\u5F62\u6210\u539F\u56E0\u4E3B\u8981\u5728\u4E8E\u6570\u636E\u7684\u65B0\u589E\u6216\u5220\u9664\uFF1A\u5728\u540C\u4E00\u4E8B\u52A1\u4E2D\uFF0C\u540C\u6837\u7684\u6761\u4EF6\uFF0C\u7B2C\u4E00\u6B21\u548C\u7B2C\u4E8C\u6B21\u8BFB\u51FA\u6765\u7684\u8BB0\u5F55\u6570\u4E0D\u4E00\u6837\u3002\uFF08\u56E0\u4E3A\u4E2D\u95F4\u6709\u5176\u4ED6\u4E8B\u52A1\u63D0\u4EA4\u4E86\u63D2\u5165/\u5220\u9664\uFF09\u3002</p><h3 id="redo-log" tabindex="-1"><a class="header-anchor" href="#redo-log" aria-hidden="true">#</a> redo log</h3><p>redo log\u662F\u91CD\u505A\u65E5\u5FD7\uFF0C\u63D0\u4F9B\u524D\u6EDA\u64CD\u4F5C\uFF0C</p><p>\u8BB0\u5F55\u7684\u662F\u6570\u636E\u4FEE\u6539\u4E4B\u540E\u7684\u503C\uFF0C\u4E0D\u7BA1\u4E8B\u52A1\u662F\u5426\u63D0\u4EA4\u90FD\u4F1A\u8BB0\u5F55\u4E0B\u6765\u3002\u5728mysql\u670D\u52A1\u5668\u5B95\u673A\u65F6\uFF0CInnoDB\u5B58\u50A8\u5F15\u64CE\u4F1A\u4F7F\u7528redo log\u6062\u590D\u5230\u6389\u7535\u524D\u7684\u65F6\u523B\uFF0C\u4EE5\u6B64\u6765\u4FDD\u8BC1\u6570\u636E\u7684\u5B8C\u6574\u6027\u3002</p><h3 id="undo-log" tabindex="-1"><a class="header-anchor" href="#undo-log" aria-hidden="true">#</a> undo log</h3><p>undo log\u662F\u56DE\u6EDA\u65E5\u5FD7\uFF0C\u63D0\u4F9B\u56DE\u6EDA\u64CD\u4F5C\u3002</p><p>\u4FDD\u5B58\u4E86\u4E8B\u52A1\u53D1\u751F\u4E4B\u524D\u7684\u6570\u636E\u7684\u4E00\u4E2A\u7248\u672C\uFF0C\u53EF\u4EE5\u7528\u4E8E\u56DE\u6EDA\uFF0C\u540C\u65F6\u53EF\u4EE5\u63D0\u4F9B\u591A\u7248\u672C\u5E76\u53D1\u63A7\u5236\u4E0B\u7684\u8BFB\uFF08MVCC\uFF09\uFF0C\u4E5F\u5373\u975E\u9501\u5B9A\u8BFB</p><h3 id="binlog" tabindex="-1"><a class="header-anchor" href="#binlog" aria-hidden="true">#</a> binlog</h3><p>binlog\u662F\u8FFD\u52A0\u65E5\u5FD7,\u53C8\u79F0\u4E3A\u5F52\u6863\u65E5\u5FD7\uFF0C\u5C5E\u4E8E\u903B\u8F91\u65E5\u5FD7\uFF0C</p><p>binlog\u662F\u5C5E\u4E8EMySQL Server\u5C42\u9762\u7684\uFF0C\u53C8\u79F0\u4E3A\u5F52\u6863\u65E5\u5FD7\uFF0C\u5C5E\u4E8E\u903B\u8F91\u65E5\u5FD7\uFF0C\u662F\u8BB0\u5F55\u539F\u59CB\u7684sql\u8BED\u53E5\uFF0C\u903B\u8F91</p><p>\u4E0D\u652F\u6301\u9AD8\u5E76\u53D1\uFF0C\u4EE5\u8BFB\u4E3A\u4E3B</p><p>\u5171\u4EAB\u9501\uFF0C\u6392\u5B83\u9501\uFF0C\u610F\u5411\u9501</p><h2 id="mvvc\u5B9E\u73B0\u539F\u7406" tabindex="-1"><a class="header-anchor" href="#mvvc\u5B9E\u73B0\u539F\u7406" aria-hidden="true">#</a> mvvc\u5B9E\u73B0\u539F\u7406</h2><p>MVCC (<code>Multi-Version Concurrency Control</code>) (\u6CE8\uFF1A\u4E0EMVCC\u76F8\u5BF9\u7684\uFF0C\u662F\u57FA\u4E8E\u9501\u7684\u5E76\u53D1\u63A7\u5236\uFF0C<code>Lock-Based Concurrency Control</code>)\u662F\u4E00\u79CD\u57FA\u4E8E\u591A\u7248\u672C\u7684\u5E76\u53D1\u63A7\u5236\u534F\u8BAE\uFF0C\u53EA\u6709\u5728<code>InnoDB</code>\u5F15\u64CE\u4E0B\u5B58\u5728\u3002 <code>MVCC</code>\u662F\u4E3A\u4E86\u5B9E\u73B0\u4E8B\u52A1\u7684\u9694\u79BB\u6027\uFF0C\u901A\u8FC7\u7248\u672C\u53F7\uFF0C\u907F\u514D\u540C\u4E00\u6570\u636E\u5728\u4E0D\u540C\u4E8B\u52A1\u95F4\u7684\u7ADE\u4E89\uFF0C\u4F60\u53EF\u4EE5\u628A\u5B83\u5F53\u6210\u57FA\u4E8E\u591A\u7248\u672C\u53F7\u7684\u4E00\u79CD\u4E50\u89C2\u9501\u3002 \u5F53\u7136\uFF0C\u8FD9\u79CD\u4E50\u89C2\u9501\u53EA\u5728\u4E8B\u52A1\u7EA7\u522B\u63D0\u4EA4\u8BFB\u548C\u53EF\u91CD\u590D\u8BFB\u6709\u6548\u3002<code>MVCC</code>\u6700\u5927\u7684\u597D\u5904\uFF0C\u76F8\u4FE1\u4E5F\u662F\u8033\u719F\u80FD\u8BE6\uFF1A\u8BFB\u4E0D\u52A0\u9501\uFF0C\u8BFB\u5199\u4E0D\u51B2\u7A81\u3002\u5728\u8BFB\u591A\u5199\u5C11\u7684OLTP\u5E94\u7528\u4E2D\uFF0C\u8BFB\u5199\u4E0D\u51B2\u7A81\u662F\u975E\u5E38\u91CD\u8981\u7684\uFF0C\u6781\u5927\u5730\u589E\u52A0\u4E86\u7CFB\u7EDF\u7684\u5E76\u53D1\u6027\u80FD\u3002</p><div class="custom-container info"><p class="custom-container-title">OLTP</p><p><code>On-Line Transaction Processing</code>\u8054\u673A\u4E8B\u52A1\u5904\u7406\u8FC7\u7A0B(OLTP)\uFF0C\u4E5F\u79F0\u4E3A\u9762\u5411\u4EA4\u6613\u7684\u5904\u7406\u8FC7\u7A0B\uFF0C\u5176\u57FA\u672C\u7279\u5F81\u662F\u524D\u53F0\u63A5\u6536\u7684\u7528\u6237\u6570\u636E\u53EF\u4EE5\u7ACB\u5373\u4F20\u9001\u5230\u8BA1\u7B97\u4E2D\u5FC3\u8FDB\u884C\u5904\u7406\uFF0C\u5E76\u5728\u5F88\u77ED\u7684\u65F6\u95F4\u5185\u7ED9\u51FA\u5904\u7406\u7ED3\u679C\uFF0C\u662F\u5BF9\u7528\u6237\u64CD\u4F5C\u5FEB\u901F\u54CD\u5E94\u7684\u65B9\u5F0F\u4E4B\u4E00\u3002</p></div><p>\u4F60\u53EF\u4EE5\u7B80\u5355\u7684\u7406\u89E3\u4E3AMVCC\u4E3A\u6BCF\u4E00\u884C\u589E\u52A0\u4E86\u4E24\u4E2A\u9690\u85CF\u5B57\u6BB5\uFF0C\u4E24\u4E2A\u5B57\u6BB5\u5206\u522B\u4FDD\u5B58\u4E86\u8FD9\u4E2A\u884C\u7684\u5F53\u524D\u4E8B\u52A1ID\u8DDF\u884C\u7684\u5220\u9664\u4E8B\u52A1ID\u3002</p><p><strong>1\u3001insert\u65F6\uFF1A</strong></p><p><code>InnoDB</code>\u4E3A\u65B0\u63D2\u5165\u7684\u6BCF\u4E00\u884C\u4FDD\u5B58\u5F53\u524D\u7CFB\u7EDF\u7248\u672C\u53F7\u4F5C\u4E3A\u7248\u672C\u53F7\u3002</p><p><strong>2\u3001select\u65F6\uFF1A</strong></p><ol><li><code>InnoDB</code>\u53EA\u4F1A\u67E5\u627E\u7248\u672C\u65E9\u4E8E\u5F53\u524D\u4E8B\u52A1\u7248\u672C\u7684\u6570\u636E\u884C(\u4E5F\u5C31\u662F\u884C\u7684\u7CFB\u7EDF\u7248\u672C\u53F7&lt;=\u4E8B\u52A1\u7684\u7CFB\u7EDF\u7248\u672C\u53F7)\uFF0C\u8FD9\u6837\u53EF\u4EE5\u786E\u4FDD\u4E8B\u52A1\u8BFB\u53D6\u7684\u884C\uFF0C\u8981\u4E48\u662F\u5728\u4E8B\u52A1\u5F00\u59CB\u524D\u5DF2\u7ECF\u5B58\u5728\u7684\uFF0C\u8981\u4E48\u662F\u4E8B\u52A1\u81EA\u8EAB\u63D2\u5165\u6216\u8005\u4FEE\u6539\u8FC7\u7684\u3002</li><li>\u884C\u7684\u5220\u9664\u7248\u672C\u8981\u4E48\u672A\u5B9A\u4E49\uFF0C\u8981\u4E48\u5927\u4E8E\u5F53\u524D\u4E8B\u52A1\u7248\u672C\u53F7\uFF0C\u8FD9\u53EF\u4EE5\u786E\u4FDD\u4E8B\u52A1\u8BFB\u53D6\u5230\u7684\u884C\u5728\u4E8B\u52A1\u5F00\u59CB\u4E4B\u524D\u672A\u88AB\u5220\u9664\u3002</li><li>\u53EA\u67091\uFF0C2 \u540C\u65F6\u6EE1\u8DB3\u7684\u8BB0\u5F55\uFF0C\u624D\u80FD\u8FD4\u56DE\u4F5C\u4E3A\u67E5\u8BE2\u7ED3\u679C\u3002</li></ol><p><strong>3\u3001delete\u65F6\uFF1A</strong></p><p>InnoDB\u4F1A\u4E3A\u5220\u9664\u7684\u6BCF\u4E00\u884C\u4FDD\u5B58\u5F53\u524D\u7CFB\u7EDF\u7684\u7248\u672C\u53F7(\u4E8B\u52A1\u7684ID)\u4F5C\u4E3A\u5220\u9664\u6807\u8BC6.</p><p><strong>4\u3001<code>update</code>\u65F6\uFF1A</strong></p><p><code>InnoDB</code>\u6267\u884C<code>update</code>\uFF0C\u5B9E\u9645\u4E0A\u662F\u65B0\u63D2\u5165\u4E86\u4E00\u884C\u8BB0\u5F55\uFF0C\u5E76\u4FDD\u5B58\u5176\u521B\u5EFA\u65F6\u95F4\u4E3A\u5F53\u524D\u4E8B\u52A1\u7684ID\uFF0C\u540C\u65F6\u4FDD\u5B58\u5F53\u524D\u4E8B\u52A1ID\u5230\u8981update\u7684\u884C\u7684\u5220\u9664\u65F6\u95F4\u3002</p><p>\u4E0A\u9762\u53EA\u662F\u4E00\u4E2A\u6D45\u663E\u5730\u8BB2\u89E3<code>MVCC</code>\u9009\u62E9\u6807\u51C6\u6D41\u7A0B\uFF0C\u6E90\u7801\u5C42\u9762\u5E94\u8BE5\u662F\u6839\u636E\u4F4E\u6C34\u4F4D\u8DDF\u9AD8\u6C34\u4F4D\u6765\u622A\u53D6\u7684\u3002\u5177\u4F53\u5B9E\u73B0\u53EF\u81EA\u884C\u767E\u5EA6\u3002</p><p>\u91CD\u70B9\uFF1A</p><ol><li>\u4E8B\u52A1\u4E2D\u5FEB\u7167\u8BFB\u7684\u7ED3\u679C\u662F\u975E\u5E38\u4F9D\u8D56\u8BE5\u4E8B\u52A1\u9996\u6B21\u51FA\u73B0\u5FEB\u7167\u8BFB\u7684\u5730\u65B9\uFF0C\u5373\u67D0\u4E2A\u4E8B\u52A1\u4E2D\u9996\u6B21\u51FA\u73B0\u5FEB\u7167\u8BFB\u7684\u5730\u65B9\u975E\u5E38\u5173\u952E\uFF0C\u5B83\u6709\u51B3\u5B9A\u8BE5\u4E8B\u52A1\u540E\u7EED\u5FEB\u7167\u8BFB\u7ED3\u679C\u7684\u80FD\u529B\u3002</li><li>\u5728<code>RC</code>\u9694\u79BB\u7EA7\u522B\u4E0B\uFF0C\u662F\u6BCF\u4E2A\u5FEB\u7167\u8BFB\u90FD\u4F1A\u751F\u6210\u5E76\u83B7\u53D6\u6700\u65B0\u7684<code>ReadView</code>\uFF1B\u800C\u5728<code>RR</code>\u9694\u79BB\u7EA7\u522B\u4E0B\uFF0C\u5219\u662F\u540C\u4E00\u4E2A\u4E8B\u52A1\u4E2D\u7684\u7B2C\u4E00\u4E2A\u5FEB\u7167\u8BFB\u624D\u4F1A\u521B\u5EFA<code>ReadView</code>, \u4E4B\u540E\u7684\u5FEB\u7167\u8BFB\u83B7\u53D6\u7684\u90FD\u662F\u540C\u4E00\u4E2A<code>ReadView</code>\u3002</li></ol><p>\u56DB\u4E2A\u9690\u5F0F\u5B57\u6BB5\uFF1A</p><ol><li><code>DB_TRX_ID</code>\uFF1A6byte\uFF0C\u6700\u8FD1\u4FEE\u6539(\u4FEE\u6539/\u63D2\u5165)\u4E8B\u52A1ID\uFF0C\u8BB0\u5F55\u521B\u5EFA\u8FD9\u6761\u8BB0\u5F55/\u6700\u540E\u4E00\u6B21\u4FEE\u6539\u8BE5\u8BB0\u5F55\u7684\u4E8B\u52A1ID\uFF1B</li><li><code>DB_ROLL_PTR</code>\uFF1A7byte\uFF0C\u56DE\u6EDA\u6307\u9488\uFF0C\u6307\u5411\u8FD9\u6761\u8BB0\u5F55\u7684\u4E0A\u4E00\u4E2A\u7248\u672C\uFF08\u5B58\u50A8\u4E8E<code>rollback segment</code>\u91CC\uFF09\uFF1B</li><li><code>DB_ROW_ID</code>\uFF1A6byte\uFF0C\u9690\u542B\u7684\u81EA\u589EID\uFF08\u9690\u85CF\u4E3B\u952E\uFF09\uFF0C\u5982\u679C\u6570\u636E\u8868\u6CA1\u6709\u4E3B\u952E\uFF0CInnoDB\u4F1A\u81EA\u52A8\u4EE5<code>DB_ROW_ID</code>\u4EA7\u751F\u4E00\u4E2A\u805A\u7C07\u7D22\u5F15\uFF1B</li><li><code>FLAG</code>\uFF1A\u4E00\u4E2A\u5220\u9664flag\u9690\u85CF\u5B57\u6BB5, \u65E2\u8BB0\u5F55\u88AB\u66F4\u65B0\u6216\u5220\u9664\u5E76\u4E0D\u4EE3\u8868\u771F\u7684\u5220\u9664\uFF0C\u800C\u662F\u5220\u9664flag\u53D8\u4E86\uFF1B</li></ol><p>\u4E8B\u52A1\u5BF9\u4E00\u6761\u8BB0\u5F55\u7684\u4FEE\u6539\uFF0C\u4F1A\u5BFC\u81F4\u8BE5\u8BB0\u5F55\u7684undo log\u6210\u4E3A\u4E00\u6761\u8BB0\u5F55\u7248\u672C\u7EBF\u6027\u8868(\u94FE\u8868)\uFF0Cundo log\u7684\u94FE\u9996\u5C31\u662F\u6700\u65B0\u7684\u65E7\u8BB0\u5F55\uFF0C\u94FE\u5C3E\u5C31\u662F\u6700\u65E9\u7684\u65E7\u8BB0\u5F55\u3002</p><p>undo\u65E5\u5FD7\uFF1A\u6B64\u77E5\u8BC6\u70B9\u4E0A\u6587\u5DF2\u7ECF\u8BF4\u8FC7\u4E86\uFF0C\u5BF9MVCC\u6709\u5E2E\u52A9\u7684\u5B9E\u8D28\u662F<code>update undo log</code>\uFF0C<code>undo log</code>\u5B9E\u9645\u4E0A\u5C31\u662F\u5B58\u5728<code>rollback segment</code>\u4E2D\u65E7\u8BB0\u5F55\u94FE\u3002</p><p>\u4E00\u81F4\u8BFB\u89C6\u56FE <code>Consistent Read View</code>\uFF1A<code>Read View</code>\u662F\u4E8B\u52A1\u8FDB\u884C\u5FEB\u7167\u8BFB\u64CD\u4F5C\u7684\u65F6\u5019\u751F\u4EA7\u7684\u8BFB\u89C6\u56FE(<code>Read View</code>)\uFF0C\u5728\u8BE5\u4E8B\u52A1\u6267\u884C\u7684\u5FEB\u7167\u8BFB\u7684\u90A3\u4E00\u523B\uFF0C\u4F1A\u751F\u6210\u6570\u636E\u5E93\u7CFB\u7EDF\u5F53\u524D\u7684\u4E00\u4E2A\u5FEB\u7167\uFF0C\u8BB0\u5F55\u5E76\u7EF4\u62A4\u7CFB\u7EDF\u5F53\u524D\u6D3B\u8DC3\u4E8B\u52A1\u7684ID(InnoDB\u91CC\u9762\u6BCF\u4E2A\u4E8B\u52A1\u6709\u4E00\u4E2A\u552F\u4E00\u7684\u4E8B\u52A1ID\uFF0C\u53EB\u4F5Ctransaction id\u3002\u5B83\u662F\u5728\u4E8B\u52A1\u5F00\u59CB\u7684\u65F6\u5019\u5411InnoDB\u7684\u4E8B\u52A1\u7CFB\u7EDF\u7533\u8BF7\u7684\uFF0C\u662F\u6309\u7533\u8BF7\u987A\u5E8F\u4E25\u683C\u9012\u589E\u7684)\u3002\u62FF\u7740\u8FD9\u4E2AID\u8DDF\u8BB0\u5F55\u4E2DID\u5BF9\u6BD4\u8FDB\u884C\u9009\u62E9\u6027\u5C55\u793A\uFF0C\u8FD9\u91CC\u8BF4\u4E0B\u5927\u81F4\u7684\u601D\u7EF4\u3002</p><h2 id="\u5F53\u524D\u8BFB" tabindex="-1"><a class="header-anchor" href="#\u5F53\u524D\u8BFB" aria-hidden="true">#</a> \u5F53\u524D\u8BFB</h2><ol><li>\u50CF<code>select lock in share mode</code>(\u5171\u4EAB\u9501)\u3001<code>select for update</code> \u3001<code>update</code>\u3001<code>insert</code>\u3001<code>delete</code>(\u6392\u4ED6\u9501)\u8FD9\u4E9B\u64CD\u4F5C\u90FD\u662F\u4E00\u79CD\u5F53\u524D\u8BFB\uFF0C\u5C31\u662F\u5B83\u8BFB\u53D6\u7684\u662F\u8BB0\u5F55\u7684\u6700\u65B0\u7248\u672C\uFF0C\u8BFB\u53D6\u65F6\u8FD8\u8981\u4FDD\u8BC1\u5176\u4ED6\u5E76\u53D1\u4E8B\u52A1\u4E0D\u80FD\u4FEE\u6539\u5F53\u524D\u8BB0\u5F55\uFF0C\u4F1A<strong>\u5BF9\u8BFB\u53D6\u7684\u8BB0\u5F55\u8FDB\u884C\u52A0\u9501</strong>\u3002</li><li>\u5F53\u524D\u8BFB\u53EF\u4EE5\u8BA4\u4E3A\u662F\u60B2\u89C2\u9501\u7684\u5177\u4F53\u529F\u80FD\u5B9E\u73B0</li></ol><h3 id="\u5FEB\u7167\u8BFB" tabindex="-1"><a class="header-anchor" href="#\u5FEB\u7167\u8BFB" aria-hidden="true">#</a> \u5FEB\u7167\u8BFB</h3><ol><li>\u4E0D\u52A0\u9501\u7684<code>select</code>\u5C31\u662F\u5FEB\u7167\u8BFB\uFF0C\u5373\u4E0D\u52A0\u9501\u7684\u975E\u963B\u585E\u8BFB\uFF1B\u5FEB\u7167\u8BFB\u7684\u524D\u63D0\u662F\u9694\u79BB\u7EA7\u522B\u4E0D\u662F\u4E32\u884C\u7EA7\u522B\uFF0C\u4E32\u884C\u7EA7\u522B\u4E0B\u7684\u5FEB\u7167\u8BFB\u4F1A\u9000\u5316\u6210\u5F53\u524D\u8BFB\uFF1B\u4E4B\u6240\u4EE5\u51FA\u73B0\u5FEB\u7167\u8BFB\u7684\u60C5\u51B5\uFF0C\u662F\u57FA\u4E8E\u63D0\u9AD8\u5E76\u53D1\u6027\u80FD\u7684\u8003\u8651\uFF0C\u5FEB\u7167\u8BFB\u7684\u5B9E\u73B0\u662F\u57FA\u4E8E\u591A\u7248\u672C\u5E76\u53D1\u63A7\u5236\uFF0C\u5373MVCC\uFF0C\u53EF\u4EE5\u8BA4\u4E3AMVCC\u662F\u884C\u9501\u7684\u4E00\u4E2A\u53D8\u79CD\uFF0C\u4F46\u5B83\u5728\u5F88\u591A\u60C5\u51B5\u4E0B\uFF0C\u907F\u514D\u4E86\u52A0\u9501\u64CD\u4F5C\uFF0C\u964D\u4F4E\u4E86\u5F00\u9500\uFF1B\u65E2\u7136\u662F\u57FA\u4E8E\u591A\u7248\u672C\uFF0C\u5373\u5FEB\u7167\u8BFB\u53EF\u80FD\u8BFB\u5230\u7684\u5E76\u4E0D\u4E00\u5B9A\u662F\u6570\u636E\u7684\u6700\u65B0\u7248\u672C\uFF0C\u800C\u6709\u53EF\u80FD\u662F\u4E4B\u524D\u7684\u5386\u53F2\u7248\u672C\u3002</li><li>\u5FEB\u7167\u8BFB\u5C31\u662F<code>MVCC</code>\u601D\u60F3\u5728MySQL\u7684\u5177\u4F53\u975E\u963B\u585E\u8BFB\u529F\u80FD\u5B9E\u73B0\uFF0C<code>MVCC</code>\u7684\u76EE\u7684\u5C31\u662F\u4E3A\u4E86\u5B9E\u73B0\u8BFB-\u5199\u51B2\u7A81\u4E0D\u52A0\u9501\uFF0C\u63D0\u9AD8\u5E76\u53D1\u8BFB\u5199\u6027\u80FD\uFF0C\u800C\u8FD9\u4E2A\u8BFB\u6307\u7684\u5C31\u662F\u5FEB\u7167\u8BFB\u3002</li></ol>',46);function c(l,a){return t}var i=o(d,[["render",c],["__file","mysql-transaction.html.vue"]]);export{i as default};
