import{b as p}from"./app.9da19d67.js";import{_ as a}from"./plugin-vue_export-helper.21dcd24c.js";const t={},e=p('<h1 id="ppt\u6280\u5DE7" tabindex="-1"><a class="header-anchor" href="#ppt\u6280\u5DE7" aria-hidden="true">#</a> ppt\u6280\u5DE7</h1><p>\u4E3A\u4E86\u4E0D\u518D\u6328\u9A82\uFF08\u4E0D\u8981\u518D\u82B1\u94B1\uFF09\uFF0C\u6211\u679C\u65AD\u5728\u7F51\u4E0A\u627E\u4E86\u4E00\u4E2A\u9AD8\u6027\u4EF7\u6BD4ppt\u8BFE\u7A0B\uFF0C\u5F53\u5929\u5C31\u62A5\u4E0A\u4E86\u540D\u5F00\u59CB\u5B66\uFF01\u4E0A\u9762\u653E\u7684\u90A3\u4E2Appt\u5C31\u662F\u6211\u5B66\u4E60\u5B8C\u8FD9\u4E2A\u8BFE\u7A0B\u540E\u505A\u51FA\u6765\u7684\u6548\u679C\uFF5E\uFF01\uFF5E\u6211\u4E0A\u7684\u90A3\u4E2A\u8BFE\u7A0B\u6211\u7ED9\u5927\u5BB6\u628A\u94FE\u63A5\u642C\u6765\u4E86\uFF0C\u5B9E\u7528\u53C8\u5E26\u52B2\uFF0C\u5B9E\u7528\u662F\u56E0\u4E3A\u8BFE\u7A0B\u4E2D\u4F1A\u624B\u628A\u624B\u6559\u5404\u79CDppt\u5C0F\u6280\u5DE7\uFF0C\u5E26\u52B2\u662F\u56E0\u4E3A\u5B66\u4E60\u65F6\u95F4\u77ED\uFF0C\u4E3B\u8981\u5728\u4E8E\u4F60\u591A\u7EC3\u4E60\uFF01\uFF01\uFF01</p><p>\u5176\u5B9E\u505Appt\u6CA1\u6709\u60F3\u8C61\u7684\u96BE\uFF0C\u6709\u4E9B\u4F4E\u7EA7\u9519\u8BEF\u662F\u53EF\u4EE5\u907F\u514D\u7684\uFF0C\u8FD9\u91CC\u7ED9\u5927\u5BB6\u8BB2\u51E0\u4E2A\u5E38\u89C1\u7684\u5F71ppt\u7F8E\u89C2\u9519\u8BEF\u64CD\u4F5C\u3002\u6587\u5B57\u592A\u591A\uFF01\u8FD9\u662F\u5927\u5BB6\u6700\u5E38\u72AF\u7684\u9519\u8BEF\uFF0CPPT\u9875\u9762\u4E0A\u5BC6\u5BC6\u9EBB\u9EBB\u7684\u6587\u5B57\uFF0C\u770B\u7684\u4EBA\u5934\u75DB\uFF0C\u5927\u5BB6\u662F\u6765\u542C\u4F60\u6F14\u8BB2\uFF0C\u53C8\u4E0D\u662F\u770B\u5C0F\u8BF4\u3002\u5982\u679C\u4F60\u5728\u6F14\u8BB2\u7684\u65F6\u5019\u9700\u8981\u8BFB\u51FA\u6765\uFF0C\u9700\u8981\u5C0F\u6284\u90A3\u8BF7\u653E\u5728\u5907\u6CE8\u91CC\u3002\uFF08\u81F3\u4E8E\u5907\u6CE8\u600E\u4E48\u52A0\u4EE5\u540E\u6709\u673A\u4F1A\u518D\u8BF4~~\uFF09</p><h2 id="\u80CC\u666F" tabindex="-1"><a class="header-anchor" href="#\u80CC\u666F" aria-hidden="true">#</a> \u80CC\u666F</h2><p>\u7A0B\u5E8F\u5458\u65E5\u5E38\u5DE5\u4F5C\u4E2D\u6700\u591A\u7684\u5E94\u8BE5\u662F\u63A5\u6536\u9700\u6C42\u3001\u7F16\u7801\u5B9E\u73B0\u9700\u6C42\u3002\u4F46\u4E5F\u6709\u4E9B\u65F6\u5019\u9700\u8981\u505A\u4E00\u4E9B\u975E\u4EE3\u7801\u7684\u6587\u5B57\u5DE5\u4F5C\u3002</p><p>\u6BD4\u5982\u505A\u8FF0\u804C\u62A5\u544A\u3001\u5DE5\u4F5C\u603B\u7ED3\u7B49\uFF0C\u8FD9\u4E9B\u4E00\u822C\u7684\u90FD\u662F\u901A\u8FC7<code>ppt</code>\u7684\u5F62\u5F0F\u8FDB\u884C\u5C55\u793A\u3002</p><p>\u8FD1\u4E00\u5E74\u6211\u505A\u4E86\u51E0\u6B21 <code>ppt</code>\uFF0C\u521A\u5F00\u59CB\u5199 <code>ppt</code> \u7B80\u76F4\u4E0D\u8981\u592A\u75DB\u82E6\uFF0C\u771F\u7684\u4E0D\u5982\u5199\u4EE3\u7801\u6765\u7684\u7B80\u5355\uFF0C\u6BCF\u665A\u71AC\u5230\u6DF1\u591C\u4F46\u4E5F\u662F\u6536\u6548\u751A\u5FAE\u3002</p><p>\u540E\u6765\u53C8\u5199\u4E86\u51E0\u6B21\uFF0C\u6162\u6162\u5730\u627E\u5230\u4E86\u4E00\u4E9B\u611F\u89C9\uFF0C\u73B0\u5728\u5C31\u8D76\u7D27\u628A\u8FD9\u4E2A\u7075\u611F\u8FDB\u884C\u603B\u7ED3\u5E76\u8BB0\u5F55\u4E0B\u6765\u3002\uFF08\u4EC5\u4E2A\u4EBA\u7406\u89E3\u54C8\uFF09</p><h2 id="\u6982\u8BBA" tabindex="-1"><a class="header-anchor" href="#\u6982\u8BBA" aria-hidden="true">#</a> \u6982\u8BBA</h2><p>\u5F53\u5199\u8FC7\u51E0\u6B21 ppt \u540E\uFF0C\u73B0\u5728\u611F\u89C9\u5199 ppt \u4E5F\u4E0D\u662F\u90A3\u4E48\u96BE\uFF08\u53EF\u80FD\u6211\u60F3\u7684\u6709\u70B9\u7B80\u5355\uFF09\u3002ppt \u5176\u5B9E\u5C31\u5305\u542B\u4E24\u4E2A\u9700\u8981\u6211\u4EEC\u6DF1\u5EA6\u601D\u8003\u7684\u4E1C\u897F\uFF0C</p><p>\u4E00\u4E2A\u662F\u5185\u5BB9\uFF0C\u4E00\u4E2A\u662F\u5F62\u5F0F\u3002</p><p>\u5185\u5BB9\u5373\u6587\u5B57-\u4F60\u8981\u8868\u8FBE\u7684\u4E1C\u897F\uFF0C\u53E6\u5916\u4E00\u4E2A\u5C31\u662F\u5F62\u5F0F\uFF0C\u5F62\u5F0F\u5C31\u662F\u5C55\u793A\u5F62\u5F0F\uFF0C\u4E5F\u5C31\u662F\u6211\u4EEC\u5E94\u8BE5\u4EE5\u600E\u6837\u7684\u7F8E\u611F\u53BB\u5C55\u793A\u6211\u4EEC\u7684\u5185\u5BB9\u3002\u4E3A\u4EC0\u4E48\u8981\u7F8E\u611F\uFF1F\u5176\u5B9E\u4E0D\u662F\u4E3A\u4E86\u81EA\u5DF1\uFF0C\u800C\u662F\u4E3A\u4E86\u4F53\u8D34\u4F60\u7684\u89C2\u4F17\uFF0C\u7528\u5FC3\u7EC4\u7EC7\u4F60\u7684\u5185\u5BB9\uFF0C\u7528\u5FC3\u8BA9\u5185\u5BB9\u53D8\u5F97\u66F4\u597D\u770B\u5C31\u662F\u5BF9\u89C2\u4F17\u7684\u4F53\u8D34\u548C\u8D1F\u8D23\uFF0C\u540C\u65F6\u4E5F\u53EF\u4EE5\u953B\u70BC\u81EA\u5DF1\u7684ppt\u80FD\u529B\u3002</p><p>\u8BB0\u5F97\u300A\u9A6C\u539F\u300B\u91CC\u6211\u4EEC\u5B66\u8FC7\u5185\u5BB9\u548C\u5F62\u5F0F\u7684\u8FA9\u8BC1\u5173\u7CFB\uFF08\u4E0B\u9762\u8FD9\u6BB5\u53EF\u4EE5\u4E0D\u770B\u54C8\uFF09\u3002</p><p>\u5185\u5BB9\u662F\u4E8B\u7269\u5B58\u5728\u7684\u57FA\u7840\u3002\u540C\u4E00\u79CD\u5185\u5BB9\u5728\u4E0D\u540C\u6761\u4EF6\u4E0B\u53EF\u4EE5\u91C7\u53D6\u4E0D\u540C\u7684\u5F62\u5F0F\uFF0C\u540C\u4E00\u79CD\u5F62\u5F0F\u5728\u4E0D\u540C\u6761\u4EF6\u4E0B\u53EF\u4EE5\u4F53\u73B0\u4E0D\u540C\u7684\u5185\u5BB9\u3002\u5185\u5BB9\u4E0E\u5F62\u5F0F\u4E92\u76F8\u8054\u7CFB\u3001\u4E92\u76F8\u5236\u7EA6\uFF0C\u5728\u4E00\u5B9A\u6761\u4EF6\u4E0B\u53EF\u4EE5\u4E92\u76F8\u8F6C\u5316\u3002\u5185\u5BB9\u51B3\u5B9A\u5F62\u5F0F\uFF0C\u5F62\u5F0F\u4F9D\u8D56\u4E8E\u5185\u5BB9\uFF0C\u5E76\u968F\u5185\u5BB9\u7684\u53D1\u5C55\u800C\u53D1\u5C55\u3002\u5F62\u5F0F\u5BF9\u5185\u5BB9\u7684\u53D1\u5C55\u8D77\u7740\u4FC3\u8FDB\u6216\u963B\u788D\u7684\u4F5C\u7528\u3002\u5185\u5BB9\u6D3B\u8DC3\uFF0C\u5F62\u5F0F\u76F8\u5BF9\u7A33\u5B9A\uFF0C\u4E24\u8005\u65E2\u76F8\u9002\u5408\u53C8\u4E0D\u7EDD\u5BF9\u9002\u5408\u3002 \u2014\u2014\u2014\u2014\u9A6C\u514B\u601D\u4E3B\u4E49\u57FA\u672C\u539F\u7406</p><p>\u90A3\u5E94\u8BE5\u600E\u6837\u786E\u5B9A\u5185\u5BB9\u548C\u4E30\u5BCC\u5185\u5BB9\u7684\u8868\u73B0\u5F62\u5F0F\u5462\uFF1F \u4E0B\u9762\u5C31\u8BF4\u8BF4\u6211\u7684\u7406\u89E3</p><h3 id="_1\u3001\u7ED3\u6784\u5316\u5185\u5BB9-\u5207\u8BB0\u957F\u7BC7\u5927\u8BBA-\u5207\u8BB0\u5806\u5B57" tabindex="-1"><a class="header-anchor" href="#_1\u3001\u7ED3\u6784\u5316\u5185\u5BB9-\u5207\u8BB0\u957F\u7BC7\u5927\u8BBA-\u5207\u8BB0\u5806\u5B57" aria-hidden="true">#</a> 1\u3001\u7ED3\u6784\u5316\u5185\u5BB9\uFF0C\u5207\u8BB0\u957F\u7BC7\u5927\u8BBA\uFF08\u5207\u8BB0\u5806\u5B57\uFF09</h3><p>\u5185\u5BB9\u5176\u5B9E\u5C31\u662F\u6211\u4EEC\u65E5\u5E38\u5DE5\u4F5C\u7684\u4E00\u4E9B\u603B\u7ED3\uFF0C\u5982\u679C\u662F\u5199\u6210\u6587\u7AE0\u7684\u8BDD\u6211\u4EEC\u5012\u662F\u53EF\u4EE5\u628A\u8981\u8868\u8FBE\u7684\u7EC6\u8282\u90FD\u5199\u4E0A\u53BB\u3002</p><p>\u4F46\u662F\u5982\u679C\u662F\u5199 ppt\uFF0C\u5C31\u8981\u628A\u7EC6\u8282\u9690\u85CF\uFF0C\u7B80\u6D01\u660E\u4E86\uFF0C\u7A81\u51FA\u4E3B\u9898\u3002\u628A\u81EA\u5DF1\u7684\u5185\u5BB9\u8FDB\u884C\u7ED3\u6784\u5316\u7684\u6574\u7406\uFF0C\u68B3\u7406\u6210\u5927\u7EB2\u3002\u6587\u5B57\u592A\u591A\u4F1A\u4E27\u5931\u7F8E\u611F\uFF0C\u964D\u4F4E\u4E13\u4E1A\u5EA6\uFF08 \u5FCD\u4F4F\uFF0C\u4E0D\u8981\u55B7\uFF09\u3002\u7EC6\u8282\u7684\u6587\u5B57\u662F\u8981\u5728\u8BB2ppt\u7684\u65F6\u5019\u8BF4\u7684\uFF0C\u5012\u662F\u53EF\u4EE5\u5199\u5230\u5907\u6CE8\u91CC\uFF0C\u60F3\u4E0D\u8D77\u6765\u4E86\u5077\u5077\u7684\u7784\u51E0\u773C\u3002</p><p>\u6BD4\u5982\u5B8C\u6210\u4E86\u4EC0\u4E48\u4E1A\u7EE9\uFF0C\u5B66\u4E60\u4E86\u54EA\u4E9B\u6280\u672F\uFF0C\u4E2A\u4EBA\u601D\u8003\u6709\u54EA\u4E9B\u7B49\uFF0C\u7136\u540E\u628A\u8FD9\u4E9B\u518D\u7EC6\u5316\u4E00\u5C42\u540E\u5C31\u662F\u5177\u4F53\u7684\u6A21\u5757\u4E86\u3002</p><p>\u6700\u540E\u5F62\u6210\u4E00\u4E2A\u7B80\u5355\u7684\u6811\u5F62\u7ED3\u6784\u3002</p><p>\u662F\u5426\u8FD8\u6709\u4E0B\u4E00\u7EA7\u6A21\u5757\u5C31\u770B\u4E2A\u4EBA\u7684\u9700\u8981\u4E86\u3002\u6BD4\u5982\u5728\u67D0\u4E2A\u4E1A\u7EE9\u91CC\u6709\u4E00\u4E9B\u7279\u522B\u7A81\u51FA\u7684\u4EAE\u70B9\uFF0C\u5C31\u53EF\u4EE5\u5355\u72EC\u7684\u5217\u51FA\u6765\uFF0C\u5728\u6F14\u8BB2\u7684\u65F6\u5019\u7740\u91CD\u7684\u4ECB\u7ECD\u4E0B\u3002</p><h3 id="_2\u3001\u786E\u5B9A\u8272\u8C03" tabindex="-1"><a class="header-anchor" href="#_2\u3001\u786E\u5B9A\u8272\u8C03" aria-hidden="true">#</a> 2\u3001\u786E\u5B9A\u8272\u8C03</h3><p>\u7F51\u7AD9\u7684UI\u6709\u57FA\u8C03\u548C\u914D\u8272\uFF0CPPT\u4E5F\u6709\uFF0C\u4E3A\u4E86\u89C6\u89C9\u7684\u8212\u9002\u548Cppt\u7684\u6574\u4F53\u6027\uFF0C\u914D\u8272\u662F\u5FC5\u987B\u7684\u3002\u4E0D\u7136\u540E\u9762\u5185\u5BB9\u7F8E\u5316\u7684\u989C\u8272\u5C31\u6CA1\u6709\u4E86\u53C2\u8003\u3002\u76F8\u4FE1\u4E00\u4F1A\u513F\u84DD\u8272\u4E00\u4F1A\u513F\u7EFF\u8272\u4E00\u4F1A\u513F\u7EA2\u8272\u7684\u8C01\u770B\u7740\u90FD\u4E0D\u4F1A\u8212\u670D\u3002</p><p>\u4E3E\u4E2A\u6817\u5B50</p><p>\u6574\u4F53\u84DD\u8272</p><p>\u6574\u4F53\u7070\u8272</p><p>\u6574\u4F53\u6DF1\u7070</p><p>\u80FD\u770B\u5230\u7A81\u51FA\u7684\u989C\u8272\u548C\u57FA\u8C03\u7684\u642D\u914D\u90FD\u662F\u6BD4\u8F83\u81EA\u7136\u7684\uFF0C\u6CA1\u6709\u51B2\u7A81\u3002</p><h3 id="_3\u3001\u7F8E\u5316\u5185\u5BB9-\u4E30\u5BCC\u8868\u73B0\u5F62\u5F0F-\u79EF\u7D2F\u7D20\u6750" tabindex="-1"><a class="header-anchor" href="#_3\u3001\u7F8E\u5316\u5185\u5BB9-\u4E30\u5BCC\u8868\u73B0\u5F62\u5F0F-\u79EF\u7D2F\u7D20\u6750" aria-hidden="true">#</a> 3\u3001\u7F8E\u5316\u5185\u5BB9\uFF08\u4E30\u5BCC\u8868\u73B0\u5F62\u5F0F\uFF0C\u79EF\u7D2F\u7D20\u6750\uFF09</h3><p>\u8FD9\u4E2A\u9636\u6BB5\u5C31\u597D\u6BD4\u6211\u4EEC\u505A\u524D\u7AEF\u5F00\u53D1\uFF0C\u9700\u8981\u7528css \u6765\u7F8E\u5316\u9875\u9762\uFF0C\u5176\u5B9E\u662F\u4E00\u4E2A\u9053\u7406\u3002\u7136\u800C\u8FD9\u4E2A\u5C55\u793A\u5F62\u5F0F\u6211\u89C9\u5F97\u662F\u6700\u96BE\u7684\uFF0C\u6700\u7E41\u7410\u7684\u3002\u56E0\u4E3A\u6CA1\u6709\u73B0\u6210\u7684\u7D20\u6750\uFF0C\u603B\u4E0D\u80FD\u628A\u6BCF\u5F20 ppt \u90FD\u641E\u6210\u53CC\u80DE\u80CE\u5427\u3002</p><p>\u8FD9\u4E2A\u65F6\u5019\u6211\u4EEC\u5DF2\u7ECF\u6709\u4E86\u7ED3\u6784\u5316\u7684\u5185\u5BB9\u548C\u57FA\u672C\u8272\u8C03\uFF0C\u73B0\u5728\u5C31\u8981\u5F00\u59CB\u5199 ppt \u4E86\u3002 \u8FD9\u4E2A\u9636\u6BB5\u4E3B\u8981\u662F\u7F8E\u5316\u548C\u4F18\u5316\u4E4B\u524D\u7684\u5185\u5BB9\uFF0C\u8BA9\u5185\u5BB9\u548C\u56FE\u5F62\u7ED3\u5408\u5C55\u793A\uFF0C\u540C\u65F6\u4E5F\u662F\u5BF9\u5185\u5BB9\u659F\u914C\u4F18\u5316\u7684\u4E00\u4E2A\u8FC7\u7A0B\u3002</p><p>\u8FD9\u91CC\u7684\u56FE\u5F62\u6307\u7684\u662F\u5F62\u72B6\u548C\u56FE\u7247\uFF0C\u5F62\u72B6\u5C31\u662F ppt \u91CC\u81EA\u5E26\u7684\u4E00\u4E9B\u5F62\u72B6\uFF0C\u56FE\u7247\u5C31\u662F\u6839\u636E\u6211\u4EEC\u8981\u8868\u8FBE\u7684\u5185\u5BB9\u800C\u786E\u5B9A\u7684\u3002</p><p>\u6211\u4E5F\u53EA\u505A\u5230\u4E86\u56FE\u5F62\u642D\u914D\u9636\u6BB5\uFF0C\u914D\u56FE\u5C31\u6BD4\u8F83\u9EBB\u70E6\uFF0C\u5982\u679C\u8981\u662F\u80FD\u786E\u5B9A\u5173\u952E\u8BCD\u4E5F\u53EF\u4EE5\uFF0C\u767E\u5EA6\u641C\u7D22\u5373\u53EF\u3002</p><p>\u5177\u4F53\u7684\u5C55\u793A\u56FE\u5F62\u53EF\u4EE5\u7528\u7CFB\u7EDF\u81EA\u5E26\u7684\u6216\u8005\u53C2\u8003\u7F51\u4E0A\u6A21\u677F\uFF0C\u4E3B\u8981\u5C31\u662F\u4E00\u4E2A\u6253\u78E8\u548C\u79EF\u7D2F\u7D20\u6750\u7684\u8FC7\u7A0B\uFF0C\u7528\u5408\u9002\u7684\u56FE\u5F62\u7EC4\u5408\u6765\u5C55\u793A\u4F60\u7684\u5185\u5BB9\u3002</p><p>\u4E3E\u51E0\u4E2A\u6817\u5B50</p><p>\u4E1A\u7EE9\u9879\u76EE\u7684\u5C55\u793A</p><p>\u81EA\u5E26\u56FE\u5F62</p><p>\u4EE5\u4E0B\u662F\u7F51\u4E0A\u7684\u6A21\u677F</p><p>\u5B8C\u6210\u8FDB\u5EA6\u5C55\u793A</p><p>\u81EA\u5E26\u56FE\u5F62</p><p>\u56FE\u6587\u7ED3\u5408</p><p>\u8868\u73B0\u5F62\u5F0F\u867D\u7136\u770B\u4E0A\u53BB\u6709\u5F88\u591A\u79CD\uFF0C\u4F46\u6309\u7167\u6BCF\u4E2A\u4EBA\u7684\u559C\u597D\u548C\u98CE\u683C\u6765\u8BF4\u5176\u5B9E\u5E76\u4E0D\u591A\uFF0C\u4E3B\u8981\u662F\u81EA\u5DF1\u4E60\u60EF\u548C\u559C\u6B22\u90A3\u79CD\u98CE\u683C\u3002\u7136\u800C\u7F51\u4E0A\u7684\u5927\u90E8\u5206\u6A21\u677F\u4E5F\u90FD\u662F\u4F7F\u7528\u56FE\u5F62\u7ED3\u5408\u5C55\u793A\uFF0C\u914D\u56FE\u7684\u4E5F\u4E0D\u591A\u3002\u800C\u914D\u56FE\u5C31\u8981\u770B\u662F\u5426\u80FD\u63D0\u70BC\u51FA\u5173\u952E\u5B57\u6216\u8005\u80FD\u591F\u627E\u4E2A\u4E00\u5F20\u548C\u5F53\u524D\u5185\u5BB9\u548C\u8C10\u5171\u5B58\u7684\u56FE\u7247\u3002</p><h2 id="_4\u3001ppt-\u5C55\u793A\u5F62\u5F0F\u4F53\u9A8C" tabindex="-1"><a class="header-anchor" href="#_4\u3001ppt-\u5C55\u793A\u5F62\u5F0F\u4F53\u9A8C" aria-hidden="true">#</a> 4\u3001ppt \u5C55\u793A\u5F62\u5F0F\u4F53\u9A8C</h2><p>\u8FD9\u6BB5\u53EF\u4EE5\u4E0D\u770B\u3002</p><p>\u5982\u679C\u4F60\u4E0D\u662F\u5F88\u4E86\u89E3ppt\u7684\u5C55\u793A\u5F62\u5F0F\uFF0C\u53EF\u4EE5\u591A\u770B\u770B\u7F51\u4E0A\u7684\u6A21\u677F\uFF0C\u5BF9\u5C55\u793A\u5F62\u5F0F\u6709\u4E2A\u5370\u8C61\u3002\u90A3\u4E48\u81EA\u5DF1\u5728\u4E30\u5BCC\u81EA\u5DF1\u7684ppt \u7684\u65F6\u5019\u5C31\u4F1A\u6709\u4E00\u4E9B\u611F\u89C9\u4E86\u3002</p><p>\u5177\u4F53\u7684\u7F51\u7AD9\u767E\u5EA6\u627E\u4E0B\u5C31\u53EF\u4EE5\u4E86\uFF0C\u5C31\u4E0D\u53D1\u5730\u5740\u4E86\u3002</p><h2 id="_5\u3001ppt-\u8D44\u6E90\u3001\u7D20\u6750\u53C2\u8003" tabindex="-1"><a class="header-anchor" href="#_5\u3001ppt-\u8D44\u6E90\u3001\u7D20\u6750\u53C2\u8003" aria-hidden="true">#</a> 5\u3001ppt \u8D44\u6E90\u3001\u7D20\u6750\u53C2\u8003</h2><p>\u7F51\u4E0A\u7684\u6A21\u677F\u57FA\u672C\u4E0A\u90FD\u662F\u6536\u8D39\u7684\uFF0C\u5F88\u65E9\u4E4B\u524D\u6211\u5728\u6DD8\u5B9D\u4E70\u8FC7\u4E00\u5806\u3002\u91CC\u9762\u6709\u5F88\u591A\u6A21\u677F\uFF0C\u6709\u9700\u8981\u53EF\u4EE5\u5B58\u4E00\u4E0B\u3002\uFF08\u6211\u4E3B\u8981\u662F\u7528\u6765\u627E\u7D20\u6750\uFF0C\u6700\u597D\u4E0D\u8981\u76F4\u63A5\u5957\u7528\uFF0C\u4E00\u4E2A\u6015\u8FC7\u65F6\uFF0C\u4E00\u4E2A\u662F\u4F1A\u5F71\u54CD\u4F60\u539F\u6709\u601D\u8DEF\uFF09</p><p>\u94FE\u63A5: https://pan.baidu.com/s/189K0... \u63D0\u53D6\u7801: ghs5</p><h2 id="\u603B\u7ED3" tabindex="-1"><a class="header-anchor" href="#\u603B\u7ED3" aria-hidden="true">#</a> \u603B\u7ED3</h2><p>\u5199ppt\u4E3B\u8981\u67093\u6B65\uFF1A</p><ol><li>\u7ED3\u6784\u5316\u5185\u5BB9\uFF08\u786E\u5B9A\u5185\u5BB9\uFF0C\u7A81\u51FA\u4E3B\u9898\uFF0C\u4E0D\u8981\u5806\u5B57\uFF09</li><li>\u786E\u5B9Appt\u98CE\u683C\uFF08\u4E5F\u5C31\u662F\u914D\u8272\uFF0C\u9EC4\u8272\u3001\u84DD\u8272\u8FD8\u662F\u4EC0\u4E48\uFF0C\u7A81\u51FAppt\u7684\u6574\u4F53\u6027\uFF09</li><li>\u7F8E\u5316\u5C55\u793A\uFF08\u6839\u636E\u914D\u8272\u8FDB\u884C\u7F8E\u5316\uFF0C\u5177\u4F53\u4F7F\u7528\u4EC0\u4E48\u56FE\u5F62\u5C55\u793A\uFF09</li></ol><p>\u7B2C\u4E00\u6B21\u5199ppt\u786E\u5B9E\u633A\u9EBB\u70E6\u7684\uFF0C\u4F46\u662F\u5F53\u81EA\u5DF1\u5B8C\u6574\u7684\u5199\u5B8C\u4E00\u4E2A\u540E\uFF0C\u5E76\u4E14\u505A\u4E86\u5927\u91CF\u7684\u4F18\u5316\u540E\uFF0C\u540E\u9762\u4F60\u4F1A\u89C9\u5F97\u5F88\u7B80\u5355\uFF0C\u4F46\u662Fppt\u8FD9\u4E2A\u5C55\u793A\u5F62\u5F0F\u6CA1\u6709\u6700\u597D\u53EA\u6709\u66F4\u597D\uFF0C\u8FD9\u4E2A\u6253\u78E8\u7684\u8FC7\u7A0B\u624D\u662F\u6700\u7D2F\u4EBA\u7684\uFF0C\u8FD9\u4E2A\u901F\u5EA6\u4E5F\u5FEB\u4E0D\u4E86\uFF0C\u4E00\u4E24\u5929\u80FD\u641E\u5B9A\u4E00\u4E2Appt\u7B97\u662F\u6BD4\u8F83\u5FEB\u7684\u4E86\uFF0C\u540E\u9762\u505A\u7684\u591A\u4E86\u5C31\u4F1A\u63D0\u5347\u4E86\u3002</p><p>\u597D\u7684\u5185\u5BB9\u548C\u8868\u73B0\u5F62\u5F0F\u8FD8\u662F\u901A\u8FC7\u81EA\u5DF1\u7684\u6DF1\u5EA6\u601D\u8003\u5F97\u6765\u7684\uFF0C\u8FD9\u91CC\u505A\u597D\u4E86\u8FD8\u4E0D\u7B97\u5B8C\uFF0C\u8FD8\u6709\u540E\u9762\u7684\u6F14\u8BB2\u9636\u6BB5\uFF0C\u6F14\u8BB2\u524D\u9700\u8981\u505A\u51E0\u6B21\u9884\u6F14\uFF0C\u5C3D\u53EF\u80FD\u7684\u51C6\u5907\u5145\u5206\uFF0C\u628A\u81EA\u5DF1\u8981\u8868\u8FBE\u7684\u5185\u5BB9\u8868\u8FBE\u51FA\u6765\uFF0C\u5C31\u66F4\u5B8C\u7F8E\u4E86\u3002</p><p>\uFF08\u672C\u6587\u662F\u5927\u80D6\u81EA\u5DF1\u7684\u7406\u89E3\uFF0C\u5927\u80D6\u7528\u7740\u8FD8\u884C\uFF0C\u5E0C\u671B\u5BF9\u4F60\u6709\u7528\uFF0C\u4E5F\u4EC5\u4EC5\u4F9B\u53C2\u8003\uFF0C\u80AF\u5B9A\u8FD8\u6709\u5176\u4ED6\u7684\u65B9\u6CD5\u548C\u89C1\u89E3\uFF0C\u6B22\u8FCE\u7559\u8A00\u5B66\u4E60\u4EA4\u6D41\u3002\uFF09</p><p>\u53E6\u5916\u548C\u5927\u5BB6\u8BF4\u4E0B\u6211\u7684\u516C\u4F17\u53F7\u201C\u91CD\u5EA6\u524D\u7AEF\u201C\u8981\u6539\u540D\u4E86\uFF0C\u6539\u6210\u201C\u524D\u7AEF\u5F20\u5927\u80D6\u201D\uFF0C\u611F\u89C9\u8FD9\u4E2A\u540D\u5B57\u66F4\u751F\u52A8\u548C\u53EF\u7231\u4E00\u4E9B\uFF0C\u4ECE\u4E00\u4E2A\u540D\u5B57\u53D8\u6210\u4E86\u4E00\u4E2A\u4EBA\uFF0C\u76EE\u524D\u5DF2\u7ECF\u63D0\u4EA4\u7533\u8BF7\u4E86\uFF0C\u8FC7\u4E24\u5929\u5C31\u53D8\u66F4\u4E86\u3002</p><p>https://zhuanlan.zhihu.com/p/90248501</p>',57);function h(r,d){return e}var n=a(t,[["render",h],["__file","ppt-production-skills.html.vue"]]);export{n as default};
