import{b as e}from"./app.9da19d67.js";import{_ as a}from"./plugin-vue_export-helper.21dcd24c.js";const p={},t=e('<h1 id="java\u96F6\u62F7\u8D1D" tabindex="-1"><a class="header-anchor" href="#java\u96F6\u62F7\u8D1D" aria-hidden="true">#</a> Java\u96F6\u62F7\u8D1D</h1><p>DMA(\u76F4\u63A5\u5B58\u50A8\u5668\u8BBF\u95EE)</p><p>DMA(Direct Memory Access\uFF0C\u76F4\u63A5\u5185\u5B58\u5B58\u53D6) \u662F\u6240\u6709\u73B0\u4EE3\u7535\u8111\u7684\u91CD\u8981\u7279\u8272\uFF0C\u5B83\u5141\u8BB8\u4E0D\u540C\u901F\u5EA6\u7684\u786C\u4EF6\u88C5\u7F6E\u6765\u6C9F\u901A\uFF0C\u800C\u4E0D\u9700\u8981\u4F9D\u8D56\u4E8E CPU \u7684\u5927\u91CF\u4E2D\u65AD\u8D1F\u8F7D\u3002\u5426\u5219\uFF0CCPU \u9700\u8981\u4ECE\u6765\u6E90\u628A\u6BCF\u4E00\u7247\u6BB5\u7684\u8D44\u6599\u590D\u5236\u5230\u6682\u5B58\u5668\uFF0C\u7136\u540E\u628A\u5B83\u4EEC\u518D\u6B21\u5199\u56DE\u5230\u65B0\u7684\u5730\u65B9\u3002\u5728\u8FD9\u4E2A\u65F6\u95F4\u4E2D\uFF0CCPU \u5BF9\u4E8E\u5176\u4ED6\u7684\u5DE5\u4F5C\u6765\u8BF4\u5C31\u65E0\u6CD5\u4F7F\u7528\u3002</p><p>\u96F6\u590D\u5236(\u82F1\u8BED\uFF1AZero-copy\uFF1B\u4E5F\u8BD1\u96F6\u62F7\u8D1D)\u6280\u672F\u662F\u6307\u8BA1\u7B97\u673A\u6267\u884C\u64CD\u4F5C\u65F6\uFF0CCPU\u4E0D\u9700\u8981\u5148\u5C06\u6570\u636E\u4ECE\u67D0\u5904\u5185\u5B58\u590D\u5236\u5230\u53E6\u4E00\u4E2A\u7279\u5B9A\u533A\u57DF\u3002\u8FD9\u79CD\u6280\u672F\u901A\u5E38\u7528\u4E8E\u901A\u8FC7\u7F51\u7EDC\u4F20\u8F93\u6587\u4EF6\u65F6\u8282\u7701CPU\u5468\u671F\u548C\u5185\u5B58\u5E26\u5BBD\u3002</p><p>1\u3001<code>MappedByteBuffer</code></p><p>java nio\u63D0\u4F9B\u7684<code>FileChannel</code>\u63D0\u4F9B\u4E86map()\u65B9\u6CD5\uFF0C\u8BE5\u65B9\u6CD5\u53EF\u4EE5\u5728\u4E00\u4E2A\u6253\u5F00\u7684\u6587\u4EF6\u548CMappedByteBuffer\u4E4B\u95F4\u5EFA\u7ACB\u4E00\u4E2A\u865A\u62DF\u5185\u5B58\u6620\u5C04\uFF0CMappedByteBuffer\u7EE7\u627F\u4E8EByteBuffer\uFF0C\u7C7B\u4F3C\u4E8E\u4E00\u4E2A\u57FA\u4E8E\u5185\u5B58\u7684\u7F13\u51B2\u533A\uFF0C\u53EA\u4E0D\u8FC7\u8BE5\u5BF9\u8C61\u7684\u6570\u636E\u5143\u7D20\u5B58\u50A8\u5728\u78C1\u76D8\u7684\u4E00\u4E2A\u6587\u4EF6\u4E2D\uFF1B\u8C03\u7528get()\u65B9\u6CD5\u4F1A\u4ECE\u78C1\u76D8\u4E2D\u83B7\u53D6\u6570\u636E\uFF0C\u6B64\u6570\u636E\u53CD\u6620\u8BE5\u6587\u4EF6\u5F53\u524D\u7684\u5185\u5BB9\uFF0C\u8C03\u7528put()\u65B9\u6CD5\u4F1A\u66F4\u65B0\u78C1\u76D8\u4E0A\u7684\u6587\u4EF6\uFF0C\u5E76\u4E14\u5BF9\u6587\u4EF6\u505A\u7684\u4FEE\u6539\u5BF9\u5176\u4ED6\u9605\u8BFB\u8005\u4E5F\u662F\u53EF\u89C1\u7684\uFF1B\u4E0B\u9762\u770B\u4E00\u4E2A\u7B80\u5355\u7684\u8BFB\u53D6\u5B9E\u4F8B\uFF0C\u7136\u540E\u5728\u5BF9MappedByteBuffer\u8FDB\u884C\u5206\u6790\uFF1A</p><p>\u5206\u522B\u63D0\u4F9B\u4E86\u4E09\u4E2A\u53C2\u6570\uFF0CMapMode\uFF0CPosition\u548Csize\uFF1B\u5206\u522B\u8868\u793A\uFF1A</p><ul><li><code>MapMode</code>\uFF1A\u6620\u5C04\u7684\u6A21\u5F0F\uFF0C\u53EF\u9009\u9879\u5305\u62EC\uFF1AREAD_ONLY\uFF0CREAD_WRITE\uFF0CPRIVATE\uFF1B</li><li><code>Position</code>\uFF1A\u4ECE\u54EA\u4E2A\u4F4D\u7F6E\u5F00\u59CB\u6620\u5C04\uFF0C\u5B57\u8282\u6570\u7684\u4F4D\u7F6E\uFF1B</li><li><code>Size</code>\uFF1A\u4ECEposition\u5F00\u59CB\u5411\u540E\u591A\u5C11\u4E2A\u5B57\u8282\uFF1B</li></ul><p>\u91CD\u70B9\u770B\u4E00\u4E0BMapMode\uFF0C\u8BF7\u4E24\u4E2A\u5206\u522B\u8868\u793A\u53EA\u8BFB\u548C\u53EF\u8BFB\u53EF\u5199\uFF0C\u5F53\u7136\u8BF7\u6C42\u7684\u6620\u5C04\u6A21\u5F0F\u53D7\u5230Filechannel\u5BF9\u8C61\u7684\u8BBF\u95EE\u6743\u9650\u9650\u5236\uFF0C\u5982\u679C\u5728\u4E00\u4E2A\u6CA1\u6709\u8BFB\u6743\u9650\u7684\u6587\u4EF6\u4E0A\u542F\u7528READ_ONLY\uFF0C\u5C06\u629B\u51FANonReadableChannelException\uFF1BPRIVATE\u6A21\u5F0F\u8868\u793A\u5199\u65F6\u62F7\u8D1D\u7684\u6620\u5C04\uFF0C\u610F\u5473\u7740\u901A\u8FC7put()\u65B9\u6CD5\u6240\u505A\u7684\u4EFB\u4F55\u4FEE\u6539\u90FD\u4F1A\u5BFC\u81F4\u4EA7\u751F\u4E00\u4E2A\u79C1\u6709\u7684\u6570\u636E\u62F7\u8D1D\u5E76\u4E14\u8BE5\u62F7\u8D1D\u4E2D\u7684\u6570\u636E\u53EA\u6709MappedByteBuffer\u5B9E\u4F8B\u53EF\u4EE5\u770B\u5230\uFF1B\u8BE5\u8FC7\u7A0B\u4E0D\u4F1A\u5BF9\u5E95\u5C42\u6587\u4EF6\u505A\u4EFB\u4F55\u4FEE\u6539\uFF0C\u800C\u4E14\u4E00\u65E6\u7F13\u51B2\u533A\u88AB\u65BD\u4EE5\u5783\u573E\u6536\u96C6\u52A8\u4F5C(garbage collected)\uFF0C\u90A3\u4E9B\u4FEE\u6539\u90FD\u4F1A\u4E22\u5931\uFF1B\u5927\u81F4\u6D4F\u89C8\u4E00\u4E0Bmap()\u65B9\u6CD5\u7684\u6E90\u7801\uFF1A</p><p>\u5927\u81F4\u610F\u601D\u5C31\u662F\u901A\u8FC7native\u65B9\u6CD5\u83B7\u53D6\u5185\u5B58\u6620\u5C04\u7684\u5730\u5740\uFF0C\u5982\u679C\u5931\u8D25\uFF0C\u624B\u52A8gc\u518D\u6B21\u6620\u5C04\uFF1B\u6700\u540E\u901A\u8FC7\u5185\u5B58\u6620\u5C04\u7684\u5730\u5740\u5B9E\u4F8B\u5316\u51FAMappedByteBuffer\uFF0CMappedByteBuffer\u672C\u8EAB\u662F\u4E00\u4E2A\u62BD\u8C61\u7C7B\uFF0C\u5176\u5B9E\u8FD9\u91CC\u771F\u6B63\u5B9E\u4F8B\u8BDD\u51FA\u6765\u7684\u662FDirectByteBuffer\uFF1B</p><p>2\u3001DirectByteBuffer</p><p>DirectByteBuffer\u7EE7\u627F\u4E8EMappedByteBuffer\uFF0C\u4ECE\u540D\u5B57\u5C31\u53EF\u4EE5\u731C\u6D4B\u51FA\u5F00\u8F9F\u4E86\u4E00\u6BB5\u76F4\u63A5\u7684\u5185\u5B58\uFF0C\u5E76\u4E0D\u4F1A\u5360\u7528jvm\u7684\u5185\u5B58\u7A7A\u95F4\uFF1B\u4E0A\u4E00\u8282\u4E2D\u901A\u8FC7Filechannel\u6620\u5C04\u51FA\u7684MappedByteBuffer\u5176\u5B9E\u9645\u4E5F\u662FDirectByteBuffer\uFF0C\u5F53\u7136\u9664\u4E86\u8FD9\u79CD\u65B9\u5F0F\uFF0C\u4E5F\u53EF\u4EE5\u624B\u52A8\u5F00\u8F9F\u4E00\u6BB5\u7A7A\u95F4\uFF1A</p><p>\u5982\u4E0A\u5F00\u8F9F\u4E86100\u5B57\u8282\u7684\u76F4\u63A5\u5185\u5B58\u7A7A\u95F4\uFF1B</p><p>3\u3001<code>Channel-to-Channel</code> \u4F20\u8F93</p><p>\u7ECF\u5E38\u9700\u8981\u4ECE\u4E00\u4E2A\u4F4D\u7F6E\u5C06\u6587\u4EF6\u4F20\u8F93\u5230\u53E6\u5916\u4E00\u4E2A\u4F4D\u7F6E\uFF0CFileChannel\u63D0\u4F9B\u4E86transferTo()\u65B9\u6CD5\u7528\u6765\u63D0\u9AD8\u4F20\u8F93\u7684\u6548\u7387\uFF0C\u9996\u5148\u770B\u4E00\u4E2A\u7B80\u5355\u7684\u5B9E\u4F8B\uFF1A</p><p>\u901A\u8FC7FileChannel\u7684transferTo()\u65B9\u6CD5\u5C06\u6587\u4EF6\u6570\u636E\u4F20\u8F93\u5230System.out\u901A\u9053\uFF0C\u63A5\u53E3\u5B9A\u4E49\u5982\u4E0B\uFF1A</p><p>\u51E0\u4E2A\u53C2\u6570\u4E5F\u6BD4\u8F83\u597D\u7406\u89E3\uFF0C\u5206\u522B\u662F\u5F00\u59CB\u4F20\u8F93\u7684\u4F4D\u7F6E\uFF0C\u4F20\u8F93\u7684\u5B57\u8282\u6570\uFF0C\u4EE5\u53CA\u76EE\u6807\u901A\u9053\uFF1BtransferTo()\u5141\u8BB8\u5C06\u4E00\u4E2A\u901A\u9053\u4EA4\u53C9\u8FDE\u63A5\u5230\u53E6\u4E00\u4E2A\u901A\u9053\uFF0C\u800C\u4E0D\u9700\u8981\u4E00\u4E2A\u4E2D\u95F4\u7F13\u51B2\u533A\u6765\u4F20\u9012\u6570\u636E\uFF1B</p><p>\u6CE8\uFF1A\u8FD9\u91CC\u4E0D\u9700\u8981\u4E2D\u95F4\u7F13\u51B2\u533A\u6709\u4E24\u5C42\u610F\u601D\uFF1A\u7B2C\u4E00\u5C42\u4E0D\u9700\u8981\u7528\u6237\u7A7A\u95F4\u7F13\u51B2\u533A\u6765\u62F7\u8D1D\u5185\u6838\u7F13\u51B2\u533A\uFF0C\u53E6\u5916\u4E00\u5C42\u4E24\u4E2A\u901A\u9053\u90FD\u6709\u81EA\u5DF1\u7684\u5185\u6838\u7F13\u51B2\u533A\uFF0C\u4E24\u4E2A\u5185\u6838\u7F13\u51B2\u533A\u4E5F\u53EF\u4EE5\u505A\u5230\u65E0\u9700\u62F7\u8D1D\u6570\u636E\uFF1B</p><h2 id="netty\u96F6\u62F7\u8D1D" tabindex="-1"><a class="header-anchor" href="#netty\u96F6\u62F7\u8D1D" aria-hidden="true">#</a> <code>Netty</code>\u96F6\u62F7\u8D1D</h2><p>netty\u63D0\u4F9B\u4E86\u96F6\u62F7\u8D1D\u7684buffer\uFF0C\u5728\u4F20\u8F93\u6570\u636E\u65F6\uFF0C\u6700\u7EC8\u5904\u7406\u7684\u6570\u636E\u4F1A\u9700\u8981\u5BF9\u5355\u4E2A\u4F20\u8F93\u7684\u62A5\u6587\uFF0C\u8FDB\u884C\u7EC4\u5408\u548C\u62C6\u5206\uFF0CNio\u539F\u751F\u7684ByteBuffer\u65E0\u6CD5\u505A\u5230\uFF0Cnetty\u901A\u8FC7\u63D0\u4F9B\u7684Composite(\u7EC4\u5408)\u548CSlice(\u62C6\u5206)\u4E24\u79CDbuffer\u6765\u5B9E\u73B0\u96F6\u62F7\u8D1D\uFF1B\u770B\u4E0B\u9762\u4E00\u5F20\u56FE\u4F1A\u6BD4\u8F83\u6E05\u6670\uFF1A</p><p>TCP\u5C42HTTP\u62A5\u6587\u88AB\u5206\u6210\u4E86\u4E24\u4E2A<code>ChannelBuffer</code>\uFF0C\u8FD9\u4E24\u4E2ABuffer\u5BF9\u6211\u4EEC\u4E0A\u5C42\u7684\u903B\u8F91(HTTP\u5904\u7406)\u662F\u6CA1\u6709\u610F\u4E49\u7684\u3002\u4F46\u662F\u4E24\u4E2A<code>ChannelBuffer</code>\u88AB\u7EC4\u5408\u8D77\u6765\uFF0C\u5C31\u6210\u4E3A\u4E86\u4E00\u4E2A\u6709\u610F\u4E49\u7684HTTP\u62A5\u6587\uFF0C\u8FD9\u4E2A\u62A5\u6587\u5BF9\u5E94\u7684ChannelBuffer\uFF0C\u624D\u662F\u80FD\u79F0\u4E4B\u4E3A\u201DMessage\u201D\u7684\u4E1C\u897F\uFF0C\u8FD9\u91CC\u7528\u5230\u4E86\u4E00\u4E2A\u8BCD\u201D<code>Virtual Buffer</code>\u201D\u3002</p><p>\u53EF\u4EE5\u770B\u4E00\u4E0Bnetty\u63D0\u4F9B\u7684CompositeChannelBuffer\u6E90\u7801\uFF1A</p><h2 id="java-nio\u4E2D\u5BF9\u96F6\u62F7\u8D1D\u7684\u4F7F\u7528\u6709\u54EA\u4E9B\u5462" tabindex="-1"><a class="header-anchor" href="#java-nio\u4E2D\u5BF9\u96F6\u62F7\u8D1D\u7684\u4F7F\u7528\u6709\u54EA\u4E9B\u5462" aria-hidden="true">#</a> Java NIO\u4E2D\u5BF9\u96F6\u62F7\u8D1D\u7684\u4F7F\u7528\u6709\u54EA\u4E9B\u5462\uFF1F</h2><p>NIO <code>DirectByteBuffer</code></p><p>Java NIO\u5F15\u5165\u4E86\u7528\u4E8E\u901A\u9053\u7684\u7F13\u51B2\u533A\u7684<code>ByteBuffer</code>\u3002 ByteBuffer\u6709\u4E09\u4E2A\u4E3B\u8981\u7684\u5B9E\u73B0\uFF1A</p><p>HeapByteBuffer</p><p>\u5728\u8C03\u7528<code>ByteBuffer.allocate()</code>\u65F6\u4F7F\u7528\u3002 \u5B83\u88AB\u79F0\u4E3A\u5806\uFF0C\u56E0\u4E3A\u5B83\u4FDD\u5B58\u5728JVM\u7684\u5806\u7A7A\u95F4\u4E2D\uFF0C\u56E0\u6B64\u60A8\u53EF\u4EE5\u83B7\u5F97\u6240\u6709\u4F18\u52BF\uFF0C\u5982GC\u652F\u6301\u548C\u7F13\u5B58\u4F18\u5316\u3002 \u4F46\u662F\uFF0C\u5B83\u4E0D\u662F\u9875\u9762\u5BF9\u9F50\u7684\uFF0C\u8FD9\u610F\u5473\u7740\u5982\u679C\u60A8\u9700\u8981\u901A\u8FC7JNI\u4E0E\u672C\u5730\u4EE3\u7801\u4EA4\u8C08\uFF0CJVM\u5C06\u4E0D\u5F97\u4E0D\u590D\u5236\u5230\u5BF9\u9F50\u7684\u7F13\u51B2\u533A\u7A7A\u95F4\u3002</p><p>DirectByteBuffer</p><p>\u5728\u8C03\u7528<code>ByteBuffer.allocateDirect()</code>\u65F6\u4F7F\u7528\u3002 JVM\u5C06\u4F7F\u7528malloc()\u5728\u5806\u7A7A\u95F4\u4E4B\u5916\u5206\u914D\u5185\u5B58\u7A7A\u95F4\u3002 \u56E0\u4E3A\u5B83\u4E0D\u662F\u7531JVM\u7BA1\u7406\u7684\uFF0C\u6240\u4EE5\u4F60\u7684\u5185\u5B58\u7A7A\u95F4\u662F\u9875\u9762\u5BF9\u9F50\u7684\uFF0C\u4E0D\u53D7GC\u5F71\u54CD\uFF0C\u8FD9\u4F7F\u5F97\u5B83\u6210\u4E3A\u5904\u7406\u672C\u5730\u4EE3\u7801\u7684\u5B8C\u7F8E\u9009\u62E9\u3002 \u7136\u800C\uFF0C\u4F60\u8981C\u7A0B\u5E8F\u5458\u4E00\u6837\uFF0C\u81EA\u5DF1\u7BA1\u7406\u8FD9\u4E2A\u5185\u5B58\uFF0C\u5FC5\u987B\u81EA\u5DF1\u5206\u914D\u548C\u91CA\u653E\u5185\u5B58\u6765\u9632\u6B62\u5185\u5B58\u6CC4\u6F0F\u3002</p><p>MappedByteBuffer</p><p>\u5728\u8C03\u7528<code>FileChannel.map()</code>\u65F6\u4F7F\u7528\u3002 \u4E0E<code>DirectByteBuffer</code>\u7C7B\u4F3C\uFF0C\u8FD9\u4E5F\u662FJVM\u5806\u5916\u90E8\u7684\u60C5\u51B5\u3002 \u5B83\u57FA\u672C\u4E0A\u4F5C\u4E3A<code>OS mmap()</code>\u7CFB\u7EDF\u8C03\u7528\u7684\u5305\u88C5\u51FD\u6570\uFF0C\u4EE5\u4FBF\u4EE3\u7801\u76F4\u63A5\u64CD\u4F5C\u6620\u5C04\u7684\u7269\u7406\u5185\u5B58\u6570\u636E\u3002</p><h2 id="\u603B\u7ED3" tabindex="-1"><a class="header-anchor" href="#\u603B\u7ED3" aria-hidden="true">#</a> \u603B\u7ED3</h2><p>\u96F6\u62F7\u8D1D\u662F\u64CD\u4F5C\u7CFB\u7EDF\u5E95\u5C42\u7684\u4E00\u79CD\u5B9E\u73B0\uFF0C\u6211\u4EEC\u5728\u7F51\u7EDC\u7F16\u7A0B\u4E2D\uFF0C\u5229\u7528\u64CD\u4F5C\u7CFB\u7EDF\u8FD9\u4E00\u7279\u6027\uFF0C\u53EF\u4EE5\u5927\u5927\u63D0\u9AD8\u6570\u636E\u4F20\u8F93\u7684\u6548\u7387\u3002\u8FD9\u4E5F\u662F\u76EE\u524D\u7F51\u7EDC\u7F16\u7A0B\u6846\u67B6\u4E2D\u90FD\u4F1A\u91C7\u7528\u7684\u65B9\u5F0F\uFF0C\u7406\u89E3\u597D\u96F6\u62F7\u8D1D\uFF0C\u6709\u52A9\u4E8E\u6211\u4EEC\u8FDB\u4E00\u6B65\u5B66\u4E60Netty\u7B49\u7F51\u7EDC\u901A\u4FE1\u6846\u67B6\u7684\u5E95\u5C42\u539F\u7406\u3002</p>',33);function r(o,f){return t}var n=a(p,[["render",r],["__file","zero-copy.html.vue"]]);export{n as default};
